import React, { useState } from "react";
import CarexLogo from "../../assets/img/carex_logo.png";
import { FaCamera, FaVideo, FaLine, FaCalendar } from 'react-icons/fa';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import axios from 'axios';

export function CreatePost() {
  const [postType, setPostType] = useState(null); // Estado para controlar o tipo de post
  const [modalOpen, setModalOpen] = useState(false); // Estado para controlar abertura do modal
  const [mediaFile, setMediaFile] = useState(null); // Estado para armazenar arquivo de mídia selecionado
  const [mediaPreview, setMediaPreview] = useState(null); // Estado para armazenar a pré-visualização da mídia
  const [eventDate, setEventDate] = useState(new Date()); // Estado para armazenar a data do evento
  const [description, setDescription] = useState(''); // Estado para armazenar a descrição do post
  const [inputValue, setInputValue] = useState(''); // Estado para armazenar o valor do input principal

  const handlePostTypeChange = (type) => {
    setPostType(type);
    setModalOpen(true); // Abre o modal quando seleciona o tipo de post
  };

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setMediaFile(file);
      setMediaPreview(URL.createObjectURL(file)); // Criar pré-visualização da mídia
    }
  };

  const handlePost = async () => {
    // Validação: Verificar se o campo principal está preenchido
    if (!inputValue.trim()) {
      alert('O campo principal deve ser preenchido.');
      return;
    }

    // Preparar dados para envio
    const formData = new FormData();
    formData.append('description', description);
    formData.append('inputValue', inputValue);
    if (mediaFile) {
      formData.append('media', mediaFile);
    }
    if (postType === 'evento') {
      formData.append('eventDate', eventDate);
    }

    // Enviar dados para o backend
    try {
      await axios.post('http://localhost:5000/create-post', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert('Postagem realizada com sucesso!');
    } catch (error) {
      console.error('Erro ao postar:', error);
      alert('Erro ao realizar postagem.');
    }

    // Resetar estados após a postagem
    setModalOpen(false);
    setMediaFile(null);
    setMediaPreview(null);
    setEventDate(new Date()); // Resetar a data do evento
    setDescription(''); // Limpar a descrição
    setInputValue(''); // Limpar o valor do input principal
  };

  const renderModalContent = () => {
    switch (postType) {
      case 'meet':
        return (
          <div>
            <h2>Criar Post Meet</h2>
            <input
              type="text"
              placeholder="Título do Meet"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
            />
            <input
              type="text"
              placeholder="Link da sala"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <input
              type="file"
              accept="image/*, video/*"
              onChange={handleMediaChange}
            />
            {mediaPreview && (
              <div>
                {mediaFile.type.startsWith('image') ? (
                  <img src={mediaPreview} alt="Preview" />
                ) : (
                  <video src={mediaPreview} controls />
                )}
              </div>
            )}
            <button onClick={handlePost}>Postar</button>
          </div>
        );
      case 'startup':
        return (
          <div>
            <h2>Criar Post StartUp</h2>
            <input
              type="text"
              placeholder="Nome da Startup"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
            />
            <textarea
              placeholder="Descrição da Startup"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <input
              type="file"
              accept="image/*, video/*"
              onChange={handleMediaChange}
            />
            {mediaPreview && (
              <div>
                {mediaFile.type.startsWith('image') ? (
                  <img src={mediaPreview} alt="Preview" />
                ) : (
                  <video src={mediaPreview} controls />
                )}
              </div>
            )}
            <button onClick={handlePost}>Postar</button>
          </div>
        );
      case 'evento':
        return (
          <div>
            <h2>Criar Post Evento</h2>
            <input
              type="text"
              placeholder="Nome do Evento"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
            />
            <div>
              <label>Data e Hora do Evento:</label>
              <DatePicker
                selected={eventDate}
                onChange={(date) => setEventDate(date)}
                showTimeSelect
                dateFormat="dd/MM/yyyy HH:mm"
                timeFormat="HH:mm"
                timeIntervals={15}
              />
            </div>
            <input
              type="text"
              placeholder="Local do Evento"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <input
              type="file"
              accept="image/*, video/*"
              onChange={handleMediaChange}
            />
            {mediaPreview && (
              <div>
                {mediaFile.type.startsWith('image') ? (
                  <img src={mediaPreview} alt="Preview" />
                ) : (
                  <video src={mediaPreview} controls />
                )}
              </div>
            )}
            <button onClick={handlePost}>Postar</button>
          </div>
        );
      case 'midia':
        return (
          <div>
            <h2>Criar Post Mídia</h2>
            <textarea
              placeholder="Descrição da Mídia"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <input
              type="file"
              accept="image/*, video/*"
              onChange={handleMediaChange}
            />
            {mediaPreview && (
              <div>
                {mediaFile.type.startsWith('image') ? (
                  <img src={mediaPreview} alt="Preview" />
                ) : (
                  <video src={mediaPreview} controls />
                )}
              </div>
            )}
            <button onClick={handlePost}>Postar</button>
          </div>
        );
      default:
        return <div>Selecione um tipo de post para começar.</div>;
    }
  };

  return (
    <div className="create-post-container">
      <div className="create-post-header">
        <img src={CarexLogo} alt="Logo" />
        <h1>Criar Novo Post</h1>
      </div>
      <div className="create-post-buttons">
        <button onClick={() => handlePostTypeChange('meet')} className="btn">
          <FaLine /> Meet
        </button>
        <button onClick={() => handlePostTypeChange('startup')} className="btn">
          <FaCamera /> Startup
        </button>
        <button onClick={() => handlePostTypeChange('evento')} className="btn">
          <FaCalendar /> Evento
        </button>
        <button onClick={() => handlePostTypeChange('midia')} className="btn">
          <FaVideo /> Mídia
        </button>
      </div>
      {modalOpen && (
        <div className="modal-overlay" onClick={() => setModalOpen(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            {renderModalContent()}
          </div>
        </div>
      )}
    </div>
  );
}
