import React, { useState } from "react";
import CarexLogo from "../../assets/img/carex_logo.png";
import { FaCamera, FaVideo, FaLine, FaCalendar } from 'react-icons/fa';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import axios from 'axios';

export function CreatePost() {
  const [postType, setPostType] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [mediaFile, setMediaFile] = useState(null);
  const [mediaPreview, setMediaPreview] = useState(null);
  const [eventDate, setEventDate] = useState(new Date());
  const [description, setDescription] = useState('');
  const [mainInput, setMainInput] = useState('');

  const handlePostTypeChange = (type) => {
    setPostType(type);
    setModalOpen(true);
  };

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setMediaFile(file);
      setMediaPreview(URL.createObjectURL(file));
    }
  };

  const handlePost = async () => {
    const postData = {
      type: postType,
      mainInput,
      description,
      eventDate,
      mediaFile: mediaFile ? URL.createObjectURL(mediaFile) : null
    };

    try {
      await axios.post('YOUR_BACKEND_URL_HERE', postData);
      alert('Postagem realizada com sucesso!');
    } catch (error) {
      console.error('Erro ao postar:', error);
      alert('Erro ao postar. Tente novamente.');
    }

    // Reset states after posting
    setModalOpen(false);
    setMediaFile(null);
    setMediaPreview(null);
    setEventDate(new Date());
    setDescription('');
    setMainInput('');
    setPostType(null);
  };

  const renderModalContent = () => {
    switch (postType) {
      case 'meet':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Meet</h2>
            <input
              type="text"
              placeholder="Título do Meet"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input
              type="text"
              placeholder="Link da sala"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
          </div>
        );
      case 'startup':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post StartUp</h2>
            <input
              type="text"
              placeholder="Nome da Startup"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Startup"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
          </div>
        );
      case 'evento':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Evento</h2>
            <input
              type="text"
              placeholder="Nome do Evento"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div className="mb-2">
              <label className="block text-white font-semibold mb-1">Data e Hora do Evento:</label>
              <DatePicker
                selected={eventDate}
                onChange={(date) => setEventDate(date)}
                showTimeSelect
                dateFormat="dd/MM/yyyy HH:mm"
                timeFormat="HH:mm"
                timeIntervals={15}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
              />
            </div>
            <input
              type="text"
              placeholder="Local do Evento"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
          </div>
        );
      case 'midias':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Mídias</h2>
            <input
              type="text"
              placeholder="Título da Mídia"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Mídia"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
          </div>
        );
      default:
        return <div>Selecione um tipo de postagem.</div>;
    }
  };

  return (
    <div className="container mx-auto p-4">
      <div className="bg-gray-900 p-4 rounded-lg shadow-md mb-4">
        <div className="flex items-center mb-4">
          <img src={CarexLogo} alt="Carex Logo" className="w-12 h-12 mr-4" />
          <input
            type="text"
            placeholder="Escreva algo..."
            value={mainInput}
            onChange={(e) => setMainInput(e.target.value)}
            className="w-full h-12 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
          />
          <button
            onClick={() => handlePost()}
            className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full ml-4"
          >
            Postar
          </button>
        </div>
        <div className="flex space-x-4">
          <button
            onClick={() => handlePostTypeChange('meet')}
            className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full flex items-center space-x-2"
          >
            <FaCamera className="text-xl" />
            <span>Meet</span>
          </button>
          <button
            onClick={() => handlePostTypeChange('startup')}
            className="bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-4 rounded-full flex items-center space-x-2"
          >
            <FaLine className="text-xl" />
            <span>StartUp</span>
          </button>
          <button
            onClick={() => handlePostTypeChange('evento')}
            className="bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded-full flex items-center space-x-2"
          >
            <FaCalendar className="text-xl" />
            <span>Evento</span>
          </button>
          <button
            onClick={() => handlePostTypeChange('midias')}
            className="bg-yellow-500 hover:bg-yellow-600 text-white font-semibold py-2 px-4 rounded-full flex items-center space-x-2"
          >
            <FaVideo className="text-xl" />
            <span>Mídias</span>
          </button>
        </div>
      </div>

      {modalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-gray-800 p-6 rounded-lg shadow-lg relative">
            <button
              onClick={() => setModalOpen(false)}
              className="absolute top-2 right-2 bg-gray-600 hover:bg-gray-700 text-white p-1 rounded-full"
            >
              ×
            </button>
            {renderModalContent()}
            <button
              onClick={handlePost}
              className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4"
            >
              Postar
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
