        );
      case 'startup':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post StartUp</h2>
            <input
              type="text"
              placeholder="Nome da Startup"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
            />
            <textarea
              placeholder="Descrição da Startup"
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'evento':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Evento</h2>
            <input
              type="text"
              placeholder="Nome do Evento"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
            />
            <div className="mb-2">
              <label className="block text-white font-semibold mb-1">Data e Hora do Evento:</label>
              <DatePicker
                selected={eventDate}
                onChange={(date) => setEventDate(date)}
                showTimeSelect
                dateFormat="dd/MM/yyyy HH:mm"
                timeFormat="HH:mm"
                timeIntervals={15}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
              />
            </div>
            <input
              type="text"
              placeholder="Local do Evento"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'midia':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Mídia</h2>
            <textarea
              placeholder="Descrição da Mídia"
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      default:
        return <div className="modal-content">Selecione um tipo de post para começar.</div>;
    }
  };

  return (
    <div className="create-post-container">
      <div className="create-post-header">
        <img src={CarexLogo} alt="Logo" className="w-12 h-12" />
        <h1 className="text-white text-2xl font-bold">Criar Novo Post</h1>
      </div>
      <div className="create-post-buttons">
        <button onClick={() => handlePostTypeChange('meet')} className="btn btn-meet">
          <FaLine className="text-blue-500" /> Meet
        </button>
        <button onClick={() => handlePostTypeChange('startup')} className="btn btn-startup">
          <FaCamera className="text-blue-500" /> Startup
        </button>
        <button onClick={() => handlePostTypeChange('evento')} className="btn btn-evento">
          <FaCalendar className="text-blue-500" /> Evento
        </button>
        <button onClick={() => handlePostTypeChange('midia')} className="btn btn-midia">
          <FaVideo className="text-blue-500" /> Mídia
        </button>
      </div>
      {modalOpen && (
        <div className="modal-overlay" onClick={() => setModalOpen(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            {renderModalContent()}
          </div>
        </div>
      )}
    </div>
  );
}
