import React, { useState } from 'react';
import { Dialog, Transition } from '@headlessui/react';
import { useNavigate } from 'react-router-dom';
import areasJSON from '../../JSON/areas.json';
import provinciasJSON from '../../JSON/provincias.json';
import nifJSON from '../../JSON/nifEmpresas.json';
import "./Register.css";
import axios from 'axios';

// Extrair os dados dos arquivos JSON
const areas = areasJSON.areas; 
const provincias = provinciasJSON.provincias; 
const validNifs = nifJSON.nifEmpresas; 

export function FormularioEmpresa() {
  const [formData, setFormData] = useState({
    Email: '',
    password: '',
    telefone: '',
    provincia: '',
    area_atuacao: '',
    nif_empresa: '',
    nome_empresa: '',
    nome_representante: '',
    ano_existencia: ''
  });

  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState(null);
  const navigate = useNavigate();

  const handleVoltar = () => {
    window.history.back(); // Isso volta para a página anterior no histórico de navegação
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validação do NIF
    if (!validNifs.includes(formData.nif_empresa)) {
      setMessage({ type: 'error', text: 'NIF inválido!' });
      setIsOpen(true);
      return;
    }

    // Validação dos outros campos
    if (!formData.email || !formData.password || !formData.telefone || !formData.provincia || !formData.area_atuacao || !formData.nif_empresa || !formData.nome_empresa || !formData.nome_representante || !formData.ano_existencia) {
      setMessage({ type: 'error', text: 'Por favor, preencha todos os campos.' });
      setIsOpen(true);
      return;
    }

    // Validação do email
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      setMessage({ type: 'error', text: 'Email inválido.' });
      setIsOpen(true);
      return;
    }

    // Validação do telefone
    if (!/^[0-9]{9}$/.test(formData.telefone)) {
      setMessage({ type: 'error', text: 'Número de telefone inválido. Deve ter 9 dígitos.' });
      setIsOpen(true);
      return;
    }

    // Validação do ano de existência
    if (formData.ano_existencia < 0) {
      setMessage({ type: 'error', text: 'O ano de existência deve ser um número positivo.' });
      setIsOpen(true);
      return;
    }

    // Envia os dados para a API
    axios.post('http://localhost:3001/register', JSON.stringify(formData), {
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(response => {
      // Simulação de sucesso de registro
      setMessage({ type: 'success', text: 'Registro bem-sucedido!' });
      setIsOpen(true);
    })
    .catch(error => {
      // Trata o erro da API
      if (error.response && error.response.status === 400) {
        setMessage({ type: 'error', text: error.response.data.message || 'Erro na validação dos dados.' });
      } else {
        setMessage({ type: 'error', text: 'Erro ao registrar. Tente novamente mais tarde.' });
      }
      setIsOpen(true);
    });
  };

  const closeModal = () => {
    setIsOpen(false);
    // Redireciona para a página de login apenas se o cadastro for bem-sucedido
    if (message && message.type === 'success') {
      navigate('/login');
      // Limpa os campos do formulário
      setFormData({
        Email: '',
        password: '',
        telefone: '',
        provincia: '',
        area_atuacao: '',
        nif_empresa: '',
        nome_empresa: '',
        nome_representante: '',
        ano_existencia: ''
      });
    }
  };

  return (
    <div className="bg-gray-900 p-8 rounded-lg shadow-lg w-full max-w-4xl mx-auto">
