import React, { useState, useEffect } from 'react';
import MainPost from '../../components/MainPost/MainPost'; // Importe o MainPost

function Feed() {
  const [postagens, setPostagens] = useState([]);
  const [filter, setFilter] = useState('todos'); // Estado para o filtro

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3001/postagens');
        const data = await response.json();
        setPostagens(data);
      } catch (error) {
        console.error('Erro ao obter as postagens:', error);
      }
    };

    fetchData();
  }, []); // Faz a requisição apenas uma vez quando o componente é montado

  return (
    <div className="feed">
      <h1>Feed de Notícias</h1>

      {/* Filtros (opcional) */}
      <div className="filter-buttons">
        <button onClick={() => setFilter('todos')}>Todos</button>
        <button onClick={() => setFilter('post')}>Postagens</button>
        <button onClick={() => setFilter('eventos')}>Eventos</button>
        <button onClick={() => setFilter('startup')}>Startups</button>
        <button onClick={() => setFilter('salasDeNegocios')}>Salas de Negócios</button>
      </div>

      {/* Renderização do MainPost com o filtro */}
      <MainPost filter={filter} /> 
    </div>
  );
}

export default Feed;
