import React from 'react';

function Postagem({ postagem }) {
  return (
    <div className="postagem p-4 rounded-lg border border-gray-200 mb-4">
      <div className="flex items-center gap-4">
        <div className="avatar">
          <img src={postagem.Usuario.Foto || 'https://ui-avatars.com/api/?name=John+Doe&size=40'} alt="Foto do autor" className="rounded-full" />
        </div>
        <div className="autor font-medium text-gray-800">
          {postagem.Usuario.Nome} - {postagem.Data}
        </div>
      </div>
      <div className="conteudo text-gray-600 mt-2">
        {postagem.Conteudo}
      </div>
      {postagem.Foto && (
        <img src={postagem.Foto} alt="Foto da postagem" className="w-full rounded-lg mt-4" />
      )}
    </div>
  );
}

export default Postagem;
