import React, { useState } from 'react';
import axios from 'axios';
import { Dialog, Transition } from '@headlessui/react';

export function Register() {
  // Estados para gerenciar os dados dos formulários
  const [usuarioData, setUsuarioData] = useState({
    Email: '',
    Endereco: '',
    Telefone: '',
    AreaAtuacao: '',
    Provincia: '',
    Senha: '',
    tipo: '', // 'empresa' ou 'empreendedor'
  });

  const [empresaData, setEmpresaData] = useState({
    NomeRepresentante: '',
    NomeEmpresa: '',
    NIF: '',
    AnosDeExistencia: '',
  });

  const [empreendedorData, setEmpreendedorData] = useState({
    Nome: '',
    Genero: '',
    DataNascimento: '',
    BI: '',
  });

  // Estados para o modal
  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState(null);

  // Função para lidar com a mudança de dados nos formulários
  const handleChange = (event) => {
    const { name, value } = event.target;
    if (name === 'tipo') {
      setUsuarioData({ ...usuarioData, [name]: value });
      if (value === 'empresa') {
        setEmpresaData({
          ...empresaData,
          NomeRepresentante: '',
          NomeEmpresa: '',
          NIF: '',
          AnosDeExistencia: '',
        });
      } else if (value === 'empreendedor') {
        setEmpreendedorData({
          ...empreendedorData,
          Nome: '',
          Genero: '',
          DataNascimento: '',
          BI: '',
        });
      }
    } else if (name.startsWith('empresaData.')) {
      const fieldName = name.replace('empresaData.', '');
      setEmpresaData({ ...empresaData, [fieldName]: value });
    } else if (name.startsWith('empreendedorData.')) {
      const fieldName = name.replace('empreendedorData.', '');
      setEmpreendedorData({ ...empreendedorData, [fieldName]: value });
    } else {
      setUsuarioData({ ...usuarioData, [name]: value });
    }
  };

  // Função para enviar os dados do formulário para a API
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Envia os dados do usuário para a API
      const response = await axios.post('http://localhost:3001/register', {
        ...usuarioData,
        ...(usuarioData.tipo === 'empresa' ? empresaData : empreendedorData),
      });
      console.log(response.data);
      // Limpa os formulários após o envio
      setUsuarioData({
        Email: '',
        Endereco: '',
        Telefone: '',
        AreaAtuacao: '',
        Provincia: '',
        Senha: '',
        tipo: '',
      });
      setEmpresaData({
        NomeRepresentante: '',
        NomeEmpresa: '',
        NIF: '',
        AnosDeExistencia: '',
      });
      setEmpreendedorData({
        Nome: '',
        Genero: '',
        DataNascimento: '',
        BI: '',
      });
      // Mostra o modal de sucesso
      setMessage({ text: 'Usuário registrado com sucesso!', type: 'success' });
      setIsOpen(true);
    } catch (error) {
      console.error(error);
      // Mostra o modal de erro
      setMessage({ text: 'Erro ao registrar o usuário. Tente novamente!', type: 'error' });
      setIsOpen(true);
    }
  };

  // Função para fechar o modal
  const closeModal = () => {
    setIsOpen(false);
    setMessage(null);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4 text-center">Cadastro de Usuário</h1>
      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <div className="mb-4">
          <label htmlFor="Email" className="block text-gray-700 text-sm font-bold mb-2">
            Email
          </label>
          <input
            type="email"
            id="Email"
            name="Email"
            value={usuarioData.Email}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="Endereco" className="block text-gray-700 text-sm font-bold mb-2">
            Endereço
          </label>
          <input
            type="text"
            id="Endereco"
            name="Endereco"
            value={usuarioData.Endereco}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="Telefone" className="block text-gray-700 text-sm font-bold mb-2">
            Telefone
          </label>
          <input
            type="tel"
            id="Telefone"
            name="Telefone"
            value={usuarioData.Telefone}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="AreaAtuacao" className="block text-gray-700 text-sm font-bold mb-2">
            Área de Atuação
          </label>
          <select
            id="AreaAtuacao"
            name="AreaAtuacao"
            value={usuarioData.AreaAtuacao}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          >
            <option value="Saúde">Saúde</option>
            <option value="Finanças">Finanças</option>
            <option value="Educação">Educação</option>
            <option value="Marketing">Marketing</option>
            <option value="Tecnologia">Tecnologia</option>
            <option value="Exportação e Importação">Exportação e Importação</option>
            <option value="Agricultura">Agricultura</option>
            <option value="Beleza">Beleza</option>
            <option value="Outro">Outro</option>
          </select>
        </div>
        <div className="mb-4">
          <label htmlFor="Provincia" className="block text-gray-700 text-sm font-bold mb-2">
            Província
          </label>
          <select
            id="Provincia"
            name="Provincia"
            value={usuarioData.Provincia}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          >
            <option value="Bié">Bié</option>
            <option value="Bengo">Bengo</option>
            <option value="Benguela">Benguela</option>
            <option value="Cabinda">Cabinda</option>
            <option value="Cuando Cubango">Cuando Cubango</option>
            <option value="Cunene">Cunene</option>
            <option value="Huíla">Huíla</option>
            <option value="Huambo">Huambo</option>
            <option value="Luanda">Luanda</option>
            <option value="Lunda Norte">Lunda Norte</option>
            <option value="Lunda Sul">Lunda Sul</option>
            <option value="Malange">Malange</option>
            <option value="Namibe">Namibe</option>
            <option value="Cuanza Sul">Cuanza Sul</option>
            <option value="Cuanza Norte">Cuanza Norte</option>
            <option value="Uíge">Uíge</option>
            <option value="Zaire">Zaire</option>
            <option value="Moxico">Moxico</option>
          </select>
        </div>
        <div className="mb-4">
          <label htmlFor="Senha" className="block text-gray-700 text-sm font-bold mb-2">
            Senha
          </label>
          <input
            type="password"
            id="Senha"
            name="Senha"
            value={usuarioData.Senha}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="tipo" className="block text-gray-700 text-sm font-bold mb-2">
            Tipo de Usuário
          </label>
          <select
            id="tipo"
            name="tipo"
            value={usuarioData.tipo}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          >
            <option value="">Selecione</option>
            <option value="empresa">Empresa</option>
            <option value="empreendedor">Empreendedor</option>
          </select>
        </div>

        {/* Campos para Empresa */}
        {usuarioData.tipo === 'empresa' && (
          <div>
            <div className="mb-4">
              <label htmlFor="empresaData.NomeRepresentante" className="block text-gray-700 text-sm font-bold mb-2">
                Nome do Representante
              </label>
              <input
                type="text"
                id="empresaData.NomeRepresentante"
                name="empresaData.NomeRepresentante"
                value={empresaData.NomeRepresentante}
                onChange={handleChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="empresaData.NomeEmpresa" className="block text-gray-700 text-sm font-bold mb-2">
                Nome da Empresa
              </label>
              <input
                type="text"
                id="empresaData.NomeEmpresa"
                name="empresaData.NomeEmpresa"
                value={empresaData.NomeEmpresa}
                onChange={handleChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="empresaData.NIF" className="block text-gray-700 text-sm font-bold mb-2">
                NIF
              </label>
              <input
                type="text"
                id="empresaData.NIF"
                name="empresaData.NIF"
                value={empresaData.NIF}
                onChange={handleChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="empresaData.AnosDeExistencia" className="block text-gray-700 text-sm font-bold mb-2">
                Anos de Existência
              </label>
              <input
                type="number"
                id="empresaData.AnosDeExistencia"
                name="empresaData.AnosDeExistencia"
                value={empresaData.AnosDeExistencia}
                onChange={handleChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>
          </div>
        )}

        {/* Campos para Empreendedor */}
        {usuarioData.tipo === 'empreendedor' && (
          <div>
            <div className="mb-4">
              <label htmlFor="empreendedorData.Nome" className="block text-gray-700 text-sm font-bold mb-2">
                Nome
              </label>
              <input
                type="text"
                id="empreendedorData.Nome"
                name="empreendedorData.Nome"
                value={empreendedorData.Nome}
                onChange={handleChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="empreendedorData.Genero" className="block text-gray-700 text-sm font-bold mb-2">
                Gênero
              </label>
              <select
                id="empreendedorData.Genero"
                name="empreendedorData.Genero"
                value={empreendedorData.Genero}
                onChange={handleChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              >
                <option value="">Selecione</option>
                <option value="Masculino">Masculino</option>
                <option value="Feminino">Feminino</option>
              </select>
            </div>
            <div className="mb-4">
              <label htmlFor="empreendedorData.DataNascimento" className="block text-gray-700 text-sm font-bold mb-2">
                Data de Nascimento
              </label>
              <input
                type="date"
                id="empreendedorData.DataNascimento"
                name="empreendedorData.DataNascimento"
                value={empreendedorData.DataNascimento}
                onChange={handleChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="empreendedorData.BI" className="block text-gray-700 text-sm font-bold mb-2">
                BI
              </label>
              <input
                type="text"
                id="empreendedorData.BI"
                name="empreendedorData.BI"
                value={empreendedorData.BI}
                onChange={handleChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>
          </div>
        )}

        <div className="flex items-center justify-between">
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Cadastrar
          </button>
        </div>
      </form>

      {/* Modal de sucesso/erro */}
      <Transition appear show={isOpen} as={React.Fragment}>
        <Dialog
          as="div"
          className="fixed inset-0 z-10 overflow-y-auto"
          onClose={closeModal}
        >
          <div className="min-h-screen px-4 text-center">
            <Transition.Child
              as={React.Fragment}
              enter="ease-out duration-900"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="ease-in duration-900"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <div className="fixed inset-0 bg-black bg-opacity-50" />
            </Transition.Child>

            <span className="inline-block h-screen align-middle" aria-hidden="true">
              ​
            </span>
            <Transition.Child
              as={React.Fragment}
              enter="ease-out duration-900"
              enterFrom="opacity-0 translate-y-4"
              enterTo="opacity-100 translate-y-0"
              leave="ease-in duration-900"
              leaveFrom="opacity-100 translate-y-0"
              leaveTo="opacity-0 translate-y-4"
            >
              <div className="inline-block w-full max-w-md p-6 my-8 overflow-hidden text-left align-middle transition-all transform bg-white shadow-xl rounded-2xl">
                <Dialog.Title
                  as="h3"
                  className={`text-lg font-medium leading-6 text-gray-900 ${
                    message?.type === 'success' ? 'text-green-500' : 'text-red-500'
                  }`}
                >
                  Informação
                </Dialog.Title>
                <div className="mt-2">
                  <p className="text-sm text-gray-500">
                    {message ? message.text : ''}
                  </p>
                </div>

                <div className="mt-4">
                  <button
                    type="button"
                    className="inline-flex justify-center px-4 py-2 text-sm font-medium text-white bg-blue-500 border border-transparent rounded-md hover:bg-blue-600 focus:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-blue-500"
                    onClick={closeModal}
                  >
                    Fechar
                  </button>
                </div>
              </div>
            </Transition.Child>
          </div>
        </Dialog>
      </Transition>
    </div>
  );
}
