import axios from 'axios'; // Importa o axios para realizar requisições HTTP

export function CreatePost() {
  const [postType, setPostType] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [mediaFile, setMediaFile] = useState(null);
  const [mediaPreview, setMediaPreview] = useState(null);
  const [eventDate, setEventDate] = useState(new Date());
  const [description, setDescription] = useState('');
  const [title, setTitle] = useState(''); // Adiciona estado para o título
  const [linkSala, setLinkSala] = useState(''); // Adiciona estado para o link da sala
  const [nomeStartup, setNomeStartup] = useState(''); // Adiciona estado para o nome da startup
  const [descricaoStartup, setDescricaoStartup] = useState(''); // Adiciona estado para a descrição da startup
  const [nomeEvento, setNomeEvento] = useState(''); // Adiciona estado para o nome do evento
  const [localEvento, setLocalEvento] = useState(''); // Adiciona estado para o local do evento
  const [userId, setUserId] = useState(null); // Estado para o userId

  useEffect(() => {
    // Buscar o userId do localStorage
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setUserId(parseInt(storedUserId));
    }
  }, []);

  const handlePostTypeChange = (type) => {
    setPostType(type);
    setModalOpen(true);
  };

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setMediaFile(file);
      setMediaPreview(URL.createObjectURL(file));
    }
  };

  const handlePost = async () => {
    // Obter os dados do post com base no tipo
    let postData = {};
    switch (postType) {
      case 'meet':
        postData = {
          type: 'meet',
          userId: userId,
          title: title,
          linkSala: linkSala,
          description: description,
          mediaFile: mediaFile
        };
        break;
      case 'startup':
        postData = {
          type: 'startup',
          userId: userId,
          title: nomeStartup,
          description: descricaoStartup,
          mediaFile: mediaFile
        };
        break;
      case 'evento':
        postData = {
          type: 'evento',
          userId: userId,
          title: nomeEvento,
          description: description,
          date: eventDate,
          local: localEvento,
          mediaFile: mediaFile
        };
        break;
      case 'midias':
        postData = {
          type: 'midias',
          userId: userId,
          title: description,
          mediaFile: mediaFile
        };
        break;
      default:
        break;
    }

    // Enviar os dados para o backend via axios
    try {
      const formData = new FormData();
      formData.append('data', JSON.stringify(postData));
      formData.append('mediaFile', mediaFile);
      const response = await axios.post('http://localhost:3001/posts', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log('Postagem criada com sucesso:', response.data);
    } catch (error) {
      console.error('Erro ao criar postagem:', error);
    }

    // Resetar estados após a postagem
    setModalOpen(false);
    setMediaFile(null);
    setMediaPreview(null);
    setEventDate(new Date());
    setDescription('');
    setTitle('');
    setLinkSala('');
    setNomeStartup('');
    setDescricaoStartup('');
    setNomeEvento('');
    setLocalEvento('');
  };

  const renderModalContent = () => {
    switch (postType) {
      case 'meet':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Meet</h2>
            <input
              type="text"
              placeholder="Título do Meet"
              value={title} // Adiciona valor para o input
              onChange={(e) => setTitle(e.target.value)} // Adiciona handler de mudança
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input
              type="text"
              placeholder="Link da sala"
              value={linkSala} // Adiciona valor para o input
              onChange={(e) => setLinkSala(e.target.value)} // Adiciona handler de mudança
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'startup':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post StartUp</h2>
            <input
              type="text"
              placeholder="Nome da Startup"
              value={nomeStartup}
              onChange={(e) => setNomeStartup(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Startup"
              value={descricaoStartup}
              onChange={(e) => setDescricaoStartup(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'evento':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Evento</h2>
            <input
              type="text"
              placeholder="Nome do Evento"
              value={nomeEvento}
              onChange={(e) => setNomeEvento(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div className="mb-2">
              <label className="block text-white font-semibold mb-1">Data e Hora do Evento:</label>
              <DatePicker
                selected={eventDate}
                onChange={(date) => setEventDate(date)}
                showTimeSelect
                dateFormat="dd/MM/yyyy HH:mm"
                timeFormat="HH:mm"
                timeIntervals={15}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
              />
            </div>
            <input
              type="text"
              placeholder="Local do Evento"
              value={localEvento}
              onChange={(e) => setLocalEvento(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'midias':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Mídias</h2>
            <textarea
              placeholder="Descrição da Mídia"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="bg-gray-900 border border-gray-700 rounded-md p-4 mb-4">
      <div className="second-col-section-home flex items-center mb-4 bg-gray-900">
        <img src={CarexLogo} alt="Carex Logo" className="w-14 h-14 rounded-full mr-4" />
        <input type="text" placeholder='Cria uma publicação' className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white" />
        <button onClick={() => setModalOpen(true)} className="ml-2 bg-blue-500 hover:bg-blue-600 text-white text-sm py-2 px-2 rounded-full">
          Postar
        </button>
      </div>
      <div className="page-home-uploads-section justify-between">
        <ul className="flex justify-between text-white font-semibold">
          <li className="flex items-center mr-1" onClick={() => handlePostTypeChange('midias')}>
            <FaCamera className="mr-1" /> Mídias
          </li>
          <li className="flex items-center px-2" onClick={() => handlePostTypeChange('meet')}>
            <FaVideo className="mr-1" /> Meet
          </li>
          <li className="flex items-center px-2" onClick={() => handlePostTypeChange('startup')}>
            <FaLine className="mr-1" /> StartUp
          </li>
          <li className="flex items-center" onClick={() => handlePostTypeChange('evento')}>
            <FaCalendar className="mr-1" /> Evento
          </li>
        </ul>
      </div>
      {modalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center">
          <div className="bg-gray-800 rounded-lg p-6 w-full max-w-lg">
            <div className="flex justify-end">
              <button onClick={() => setModalOpen(false)} className="text-white font-bold">
                ×
              </button>
            </div>
            {renderModalContent()}
          </div>
        </div>
      )}
    </div>
  );
}
