import React, { useState } from 'react';

function Filter({ onFilterChange }) {
  const [activeFilter, setActiveFilter] = useState('todos');

  const handleFilterClick = (filter) => {
    setActiveFilter(filter);
    onFilterChange(filter);
  };

  return (
    <div className="flex gap-2">
      <button 
        className={`bg-gray-700 text-white px-4 py-2 rounded-md hover:bg-gray-600 ${activeFilter === 'todos' ? 'bg-blue-500' : ''}`}
        onClick={() => handleFilterClick('todos')}
      >
        Todos
      </button>
      <button 
        className={`bg-gray-700 text-white px-4 py-2 rounded-md hover:bg-gray-600 ${activeFilter === 'postagem' ? 'bg-blue-500' : ''}`}
        onClick={() => handleFilterClick('postagem')}
      >
        Postagem
      </button>
      <button 
        className={`bg-gray-700 text-white px-4 py-2 rounded-md hover:bg-gray-600 ${activeFilter === 'startup' ? 'bg-blue-500' : ''}`}
        onClick={() => handleFilterClick('startup')}
      >
        Startup
      </button>
      <button 
        className={`bg-gray-700 text-white px-4 py-2 rounded-md hover:bg-gray-600 ${activeFilter === 'evento' ? 'bg-blue-500' : ''}`}
        onClick={() => handleFilterClick('evento')}
      >
        Evento
      </button>
      <button 
        className={`bg-gray-700 text-white px-4 py-2 rounded-md hover:bg-gray-600 ${activeFilter === 'meet' ? 'bg-blue-500' : ''}`}
        onClick={() => handleFilterClick('meet')}
      >
        Meet
      </button>
    </div>
  );
}

function ParentComponent() {
  const [activeFilter, setActiveFilter] = useState('todos');
  const [posts, setPosts] = useState([
    { id: 1, type: 'postagem', content: 'Postagem 1' },
    { id: 2, type: 'startup', content: 'Startup 2' },
    { id: 3, type: 'evento', content: 'Evento 3' },
    { id: 4, type: 'meet', content: 'Meet 4' },
    // ... outros posts ...
  ]);

  const handleFilterChange = (filter) => {
    setActiveFilter(filter);
    const filteredPosts = filter === 'todos'
      ? posts
      : posts.filter(post => post.type === filter);
    setPosts(filteredPosts);
  };

  return (
    <div>
      <Filter onFilterChange={handleFilterChange} /> 
      <ul>
        {posts.map(post => (
          <li key={post.id}>
            {post.content}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ParentComponent;
