import React, { useState } from 'react';

export function CreatePost() {
  const [postContent, setPostContent] = useState('');

  const handleChange = (event) => {
    setPostContent(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle post submission here, e.g., send to backend
    console.log('Post submitted:', postContent);
    setPostContent('');
  };

  return (
    <div className="bg-gray-900 rounded-lg p-4 w-full max-w-md mx-auto">
      <form onSubmit={handleSubmit}>
        <div className="flex items-center gap-2">
          <img src="/user.png" alt="User avatar" className="w-10 h-10 rounded-full" />
          <textarea
            rows={4}
            placeholder="Comece uma publicação"
            className="w-full resize-none rounded-lg border border-gray-700 focus:ring-2 focus:ring-blue-500 p-2"
            value={postContent}
            onChange={handleChange}
          />
        </div>
        <div className="flex mt-4 gap-4">
          <button type="button" className="flex items-center gap-2 bg-gray-800 text-white px-4 py-2 rounded-lg hover:bg-gray-700">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l.879.879a2 2 0 010 2.828l-.879.879m-4 0l.879.879a2 2 0 010 2.828l-.879.879m-6 0l.879.879a2 2 0 010 2.828l-.879.879" /></svg>
            Mídia
          </button>
          <button type="button" className="flex items-center gap-2 bg-gray-800 text-white px-4 py-2 rounded-lg hover:bg-gray-700">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 0 0-2-2H7a2 2 0 0 0-2 2v16M21 21H3" /></svg>
            Vaga
          </button>
          <button type="button" className="flex items-center gap-2 bg-gray-800 text-white px-4 py-2 rounded-lg hover:bg-gray-700">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" /></svg>
            Startup
          </button>
          <button type="button" className="flex items-center gap-2 bg-gray-800 text-white px-4 py-2 rounded-lg hover:bg-gray-700">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
            Sala de negócios
          </button>
          <button type="submit" className="flex items-center gap-2 bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600">
            Publicar
          </button>
        </div>
      </form>
    </div>
  );
}
