import React, { useState } from 'react';
import { PrincipalPage } from '../../layout/principalPage';
import { FaUserCircle, FaCommentDots } from 'react-icons/fa';

export function ChatsPage() {
  const [messages, setMessages] = useState([
    { sender: 'Você', message: 'Olá!', timestamp: '10:00 AM' },
    { sender: 'Usuário', message: 'Oi! Tudo bem?', timestamp: '10:05 AM' },
  ]);

  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim() !== '') {
      setMessages([
        ...messages,
        { sender: 'Você', message: newMessage, timestamp: 'Agora' },
      ]);
      setNewMessage('');
    }
  };

  return (
    <PrincipalPage>
    <div className="bg-gray-800 min-h-screen p-4 rounded-md">
      <h2 className="text-white text-2xl font-bold mb-4">Mensagens</h2>

      <div className="messages-container overflow-y-auto max-h-96">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`message-item flex items-center mb-2 ${
              message.sender === 'Você' ? 'justify-end' : 'justify-start'
            }`}
          >
            <div
              className={`message-bubble bg-gray-700 text-white p-3 rounded-md ${
                message.sender === 'Você' ? 'ml-auto' : ''
              }`}
            >
              <p className="text-sm">{message.message}</p>
              <span className="text-xs text-gray-400">{message.timestamp}</span>
            </div>
          </div>
        ))}
      </div>

      <div className="input-container flex mt-4">
        <input
          type="text"
          className="bg-gray-700 text-white border border-gray-600 rounded-md px-3 py-2 flex-grow"
          placeholder="Digite sua mensagem..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded-md ml-2 hover:bg-blue-600"
          onClick={handleSendMessage}
        >
          Enviar
        </button>
      </div>
    </div>
    </PrincipalPage>
  );
}
