import React, { useState, useEffect } from "react";
import CarexLogo from "../../assets/img/carex_logo.png";
import { FaCamera, FaVideo, FaLine, FaCalendar } from 'react-icons/fa';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export function CreatePost() {
  const [postType, setPostType] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [mediaFile, setMediaFile] = useState(null);
  const [mediaPreview, setMediaPreview] = useState(null);
  const [eventDate, setEventDate] = useState(new Date());
  const [description, setDescription] = useState('');

  useEffect(() => {
    // Limpar o URL da mídia ao desmontar o componente
    return () => {
      if (mediaPreview) {
        URL.revokeObjectURL(mediaPreview);
      }
    };
  }, [mediaPreview]);

  const handlePostTypeChange = (type) => {
    setPostType(type);
    setModalOpen(true);
  };

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setMediaFile(file);
      setMediaPreview(URL.createObjectURL(file));
    }
  };

  const handlePost = () => {
    // Lógica para enviar o post (adicionar implementação de envio aqui)
    // Resetar estados após a postagem
    setModalOpen(false);
    setMediaFile(null);
    setMediaPreview(null);
    setEventDate(new Date());
    setDescription('');
  };

  const renderModalContent = () => {
    switch (postType) {
      case 'meet':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Meet</h2>
            <input
              type="text"
              placeholder="Título do Meet"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input
              type="text"
              placeholder="Link da sala"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'startup':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post StartUp</h2>
            <input
              type="text"
              placeholder="Nome da Startup"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Startup"
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'evento':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Evento</h2>
            <input
              type="text"
              placeholder="Nome do Evento"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div className="mb-2">
              <label className="block text-white font-semibold mb-1">Data e Hora do Evento:</label>
              <DatePicker
                selected={eventDate}
                onChange={(date) => setEventDate(date)}
                showTimeSelect
                dateFormat="dd/MM/yyyy HH:mm"
                timeFormat="HH:mm"
                timeIntervals={15}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
              />
            </div>
            <input
              type="text"
              placeholder="Local do Evento"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'mídia':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Mídia</h2>
            <input
              type="text"
              placeholder="Título da Mídia"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Mídia"
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-around mb-4">
        <button onClick={() => handlePostTypeChange('meet')} className="btn-post">
          <FaLine /> Meet
        </button>
        <button onClick={() => handlePostTypeChange('startup')} className="btn-post">
          <FaCamera /> StartUp
        </button>
        <button onClick={() => handlePostTypeChange('evento')} className="btn-post">
          <FaCalendar /> Evento
        </button>
        <button onClick={() => handlePostTypeChange('mídia')} className="btn-post">
          <FaVideo /> Mídia
        </button>
      </div>
      {modalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2 className="text-white font-bold text-xl mb-4">Criar Post</h2>
              <button onClick={() => setModalOpen(false)} className="text-white">
                Fechar
              </button>
            </div>
            {renderModalContent()}
          </div>
        </div>
      )}
    </div>
  );
}
