import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

// Cria o contexto
const AuthContext = createContext();

// Componente Provedor de Contexto
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [userType, setUserType] = useState(null); 
  const [error, setError] = useState(null); 

  // Função para logar o usuário
  const login = async (email, password) => {
    try {
      const response = await axios.post("http://localhost:3001/login", {
        Email: email,
        Senha: password,
      });

      if (response.status === 200) { 
        setToken(response.data.token);
        localStorage.setItem("token", response.data.token);

        if (response.data.user && response.data.user.tipo) { 
          setUser(response.data.user); 
          setUserType(response.data.user.tipo); 
          setError(null); 
          console.log("user: ", response.data);
          console.log("userType: ", response.data.user.tipo);
        } else {
          console.error('Propriedade "tipo" não encontrada na resposta da API'); 
        }
      } else {
        setError(response.data.error || 'Credenciais inválidas');
      }

    } catch (error) {
      console.error("Erro ao logar:", error);
      setError(error.response?.data?.error || 'Erro ao fazer login. Tente novamente mais tarde.');
    }
  };

  // Função para sair da conta
  const logout = () => {
    setToken(null);
    setUser(null);
    setUserType(null); 
    setError(null); 
    localStorage.removeItem("token");
  };

  // Obter as informações do usuário logado
  const getUser = () => {
    return user;
  };

  // Verificar se o usuário está logado quando o componente é montado
  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      setToken(storedToken);
      // Fazer uma requisição para obter as informações do usuário (verifique sua API)
      // Só faz a requisição se o usuário está logado (token válido)
      axios
        .get(`http://localhost:3001/usuarios/`, {
          headers: {
            Authorization: `Bearer ${storedToken}`,
          },
        })
        .then((response) => {
          setUser(response?.data);
          setUserType(response?.data?.tipo);
          setError(null);
        })
        .catch((error) => {
          console.error("Erro ao obter informações do usuário:", error);
          logout(); 
        });
    }
  }, []);

  return (
    <AuthContext.Provider
      value={{ user, token, userType, error, login, logout, getUser }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export { AuthContext };
