import React, { useState } from 'react';

export function CreatePost() {
  const [postContent, setPostContent] = useState('');
  const [postImage, setPostImage] = useState(null);
  const [postFiles, setPostFiles] = useState([]);

  const handleChangeContent = (event) => {
    setPostContent(event.target.value);
  };

  const handleChangeImage = (event) => {
    setPostImage(event.target.files[0]);
  };

  const handleChangeFiles = (event) => {
    setPostFiles(Array.from(event.target.files));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Criar um objeto com os dados do post
    const postData = new FormData();
    postData.append('content', postContent);
    if (postImage) postData.append('image', postImage);
    postFiles.forEach((file) => postData.append('files', file));

    // Fazer a requisição para o backend
    try {
      const response = await fetch('/api/posts', { // Substitua '/api/posts' pelo endpoint da sua API
        method: 'POST',
        body: postData,
      });

      if (response.ok) {
        // O post foi criado com sucesso!
        console.log('Post criado com sucesso!');
        // Limpar os campos do formulário
        setPostContent('');
        setPostImage(null);
        setPostFiles([]);
      } else {
        // Houve um erro ao criar o post
        console.error('Erro ao criar o post:', response.status);
        // Exibir uma mensagem de erro para o usuário
      }
    } catch (error) {
      console.error('Erro na requisição:', error);
      // Exibir uma mensagem de erro para o usuário
    }
  };

  return (
    <div className="bg-gray-900 rounded-lg p-4 w-full max-w-md mx-auto">
      <form onSubmit={handleSubmit}>
        <div className="flex items-center gap-2">
          <img src="/user.png" alt="User avatar" className="w-10 h-10 rounded-full" />
          <textarea
            rows={4}
            placeholder="Comece uma publicação"
            className="w-full resize-none rounded-lg border border-gray-700 focus:ring-2 focus:ring-blue-500 p-2"
            value={postContent}
            onChange={handleChangeContent}
          />
        </div>
        <div className="flex mt-4 gap-4">
          <div>
            <label htmlFor="postImage" className="text-white block mb-1 font-medium">Imagem:</label>
            <input
              type="file"
              id="postImage"
              accept="image/*"
              onChange={handleChangeImage}
              className="w-full border border-gray-700 px-3 py-2 rounded-lg focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div>
            <label htmlFor="postFiles" className="text-white block mb-1 font-medium">Arquivos:</label>
            <input
              type="file"
              id="postFiles"
              multiple
              onChange={handleChangeFiles}
              className="w-full border border-gray-700 px-3 py-2 rounded-lg focus:ring-2 focus:ring-blue-500"
            />
          </div>
        </div>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 mt-4">
          Publicar
        </button>
      </form>
    </div>
  );
}
