import axios from 'axios'; // Importar o axios para a conexão direta com o backend

export function CreatePost() {
  const [postType, setPostType] = useState(null); // Estado para controlar o tipo de post
  const [modalOpen, setModalOpen] = useState(false); // Estado para controlar abertura do modal
  const [mediaFile, setMediaFile] = useState(null); // Estado para armazenar arquivo de mídia selecionado
  const [mediaPreview, setMediaPreview] = useState(null); // Estado para armazenar a pré-visualização da mídia
  const [eventDate, setEventDate] = useState(new Date()); // Estado para armazenar a data do evento
  const [description, setDescription] = useState(''); // Estado para armazenar a descrição do post
  const [mainInput, setMainInput] = useState(''); // Estado para armazenar o input principal

  const handlePostTypeChange = (type) => {
    setPostType(type);
    setModalOpen(true); // Abre o modal quando seleciona o tipo de post
  };

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setMediaFile(file);
      setMediaPreview(URL.createObjectURL(file)); // Criar pré-visualização da mídia
    }
  };

  const handlePost = async () => {
    // Validar input principal
    if (!mainInput.trim()) {
      alert('O input principal não pode estar vazio.');
      return;
    }

    // Validar campos específicos de cada tipo de post
    if (postType === 'meet' && (!description.trim() || !description.trim())) {
      alert('Por favor, preencha todos os campos para o Meet.');
      return;
    }

    if (postType === 'startup' && (!description.trim())) {
      alert('Por favor, preencha todos os campos para a Startup.');
      return;
    }

    if (postType === 'evento' && (!description.trim() || !eventDate)) {
      alert('Por favor, preencha todos os campos para o Evento.');
      return;
    }

    if (postType === 'midias' && !description.trim()) {
      alert('Por favor, preencha todos os campos para Mídias.');
      return;
    }

    // Criar o objeto para o post
    const postData = {
      type: postType,
      mainInput,
      description,
      eventDate,
      mediaFile: mediaFile ? URL.createObjectURL(mediaFile) : null
    };

    // Enviar dados para o backend usando axios
    try {
      await axios.post('YOUR_BACKEND_URL_HERE', postData);
      alert('Postagem realizada com sucesso!');
    } catch (error) {
      console.error('Erro ao postar:', error);
      alert('Erro ao postar. Tente novamente.');
    }

    // Resetar estados após a postagem
    setModalOpen(false);
    setMediaFile(null);
    setMediaPreview(null);
    setEventDate(new Date()); // Resetar a data do evento
    setDescription(''); // Limpar a descrição
    setMainInput(''); // Limpar o input principal
    setPostType(null); // Resetar o tipo de post
  };

  const renderModalContent = () => {
    switch (postType) {
      case 'meet':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Meet</h2>
            <input
              type="text"
              placeholder="Título do Meet"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input
              type="text"
              placeholder="Link da sala"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'startup':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post StartUp</h2>
            <input
              type="text"
              placeholder="Nome da Startup"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Startup"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'evento':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Evento</h2>
            <input
              type="text"
              placeholder="Nome do Evento"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div className="mb-2">
              <label className="block text-white font-semibold mb-1">Data e Hora do Evento:</label>
              <DatePicker
                selected={eventDate}
                onChange={(date) => setEventDate(date)}
                showTimeSelect
                dateFormat="dd/MM/yyyy HH:mm"
                timeFormat="HH:mm"
                timeIntervals={15}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
              />
            </div>
            <input
              type="text"
              placeholder="Local do Evento"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'midias':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Mídias</h2>
            <input
              type="text"
              placeholder="Título da Mídia"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Mídia"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      default:
        return <div>Selecione um tipo de postagem.</div>;
    }
  };

  return (
    <div className="container mx-auto p-4">
      <div className="bg-gray-900 p-4 rounded-lg shadow-md">
        <div className="flex items-center mb-4">
          <img src={CarexLogo} alt="Carex Logo" className="w-12 h-12 mr-4" />
          <input
            type="text"
            placeholder="Escreva algo..."
            value={mainInput}
            onChange={(e) => setMainInput(e.target.value)}
            className="w-full h-12 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
          />
        </div>
        <div className="flex space-x-4">
          <button
            onClick={() => handlePostTypeChange('meet')}
            className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full flex items-center space-x-2"
          >
            <FaCamera className="text-xl" />
            <span>Meet</span>
          </button>
          <button
            onClick={() => handlePostTypeChange('startup')}
            className="bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-4 rounded-full flex items-center space-x-2"
          >
            <FaLine className="text-xl" />
            <span>StartUp</span>
          </button>
          <button
            onClick={() => handlePostTypeChange('evento')}
            className="bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded-full flex items-center space-x-2"
          >
            <FaCalendar className="text-xl" />
            <span>Evento</span>
          </button>
          <button
            onClick={() => handlePostTypeChange('midias')}
            className="bg-yellow-500 hover:bg-yellow-600 text-white font-semibold py-2 px-4 rounded-full flex items-center space-x-2"
          >
            <FaVideo className="text-xl" />
            <span>Mídias</span>
          </button>
        </div>
      </div>

      {modalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-gray-800 p-6 rounded-lg shadow-lg">
            {renderModalContent()}
            <button
              onClick={() => setModalOpen(false)}
              className="bg-gray-500 hover:bg-gray-600 text-white font-semibold py-2 px-4 rounded-full mt-4"
            >
              Fechar
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
