import React, { useState } from "react";
import CarexLogo from "../../assets/img/carex_logo.png";
import { FaCamera, FaVideo, FaLine, FaCalendar } from 'react-icons/fa';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import axios from 'axios';

export function CreatePost() {
  const [postType, setPostType] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [mediaFile, setMediaFile] = useState(null);
  const [mediaPreview, setMediaPreview] = useState(null);
  const [eventDate, setEventDate] = useState(new Date());
  const [description, setDescription] = useState('');
  const [mainInput, setMainInput] = useState('');

  const handlePostTypeChange = (type) => {
    setPostType(type);
    setModalOpen(true);
  };

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setMediaFile(file);
      setMediaPreview(URL.createObjectURL(file));
    }
  };

  const handlePost = async () => {
    const formData = new FormData();
    formData.append('type', postType);
    formData.append('mainInput', mainInput);
    formData.append('description', description);
    formData.append('eventDate', eventDate);
    if (mediaFile) {
      formData.append('mediaFile', mediaFile);
    }

    try {
      await axios.post('YOUR_BACKEND_URL_HERE', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('Postagem realizada com sucesso!');
    } catch (error) {
      console.error('Erro ao postar:', error);
      alert('Erro ao postar. Tente novamente.');
    }

    // Reset states after posting
    setModalOpen(false);
    setMediaFile(null);
    setMediaPreview(null);
    setEventDate(new Date());
    setDescription('');
    setMainInput('');
    setPostType(null);
  };

  const renderModalContent = () => {
    switch (postType) {
      case 'meet':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Meet</h2>
            <input
              type="text"
              placeholder="Título do Meet"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input
              type="text"
              placeholder="Link da sala"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
          </div>
        );
      case 'startup':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post StartUp</h2>
            <input
              type="text"
              placeholder="Nome da Startup"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Startup"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
          </div>
        );
      case 'evento':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Evento</h2>
            <input
              type="text"
              placeholder="Nome do Evento"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div className="mb-2">
              <label className="block text-white font-semibold mb-1">Data e Hora do Evento:</label>
              <DatePicker
                selected={eventDate}
                onChange={(date) => setEventDate(date)}
                showTimeSelect
                dateFormat="dd/MM/yyyy HH:mm"
                timeFormat="HH:mm"
                timeIntervals={15}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
              />
            </div>
            <input
              type="text"
              placeholder="Local do Evento"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
          </div>
        );
      case 'midias':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Mídias</h2>
            <input
              type="text"
              placeholder="Título da Mídia"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Mídia"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="create-post-container">
      <div className="post-buttons">
        <button onClick={() => handlePostTypeChange('meet')} className="post-button">
          <FaCamera /> Meet
        </button>
        <button onClick={() => handlePostTypeChange('startup')} className="post-button">
          <FaVideo /> Startup
        </button>
        <button onClick={() => handlePostTypeChange('evento')} className="post-button">
          <FaCalendar /> Evento
        </button>
        <button onClick={() => handlePostTypeChange('midias')} className="post-button">
          <FaLine /> Mídias
        </button>
      </div>
      {modalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2 className="text-white text-xl font-bold">Criar Novo Post</h2>
              <button onClick={() => setModalOpen(false)} className="modal-close">×</button>
            </div>
            {renderModalContent()}
            <div className="modal-footer">
              <button onClick={handlePost} className="modal-save">Salvar</button>
              <button onClick={() => setModalOpen(false)} className="modal-cancel">Cancelar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
