import React, { useState } from "react";
import { FaCamera, FaVideo, FaCalendar, FaMeetup } from "react-icons/fa";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export function CreatePost() {
  const [postType, setPostType] = useState("text");
  const [postText, setPostText] = useState("");
  const [mediaFile, setMediaFile] = useState(null);
  const [mediaPreview, setMediaPreview] = useState(null);
  const [eventDate, setEventDate] = useState(new Date());
  const [modalOpen, setModalOpen] = useState(false);

  const handlePostTypeChange = (type) => {
    setPostType(type);
    setModalOpen(true);
  };

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setMediaFile(file);
      setMediaPreview(URL.createObjectURL(file));
    }
  };

  const handlePostSubmit = (event) => {
    event.preventDefault();

    // Implemente o tratamento do envio de dados conforme necessário
    alert('Postagem criada com sucesso!');

    // Limpar os campos após a submissão
    setPostType("text");
    setPostText("");
    setMediaFile(null);
    setMediaPreview(null);
    setEventDate(new Date());
    setModalOpen(false);
  };

  const renderModalContent = () => {
    switch (postType) {
      case "photo":
        return (
          <div className="modal-content">
            <label
              htmlFor="mediaUpload"
              className="block text-white font-semibold mb-1 cursor-pointer"
            >
              Upload de Foto:
            </label>
            <input
              type="file"
              id="mediaUpload"
              accept="image/*"
              onChange={handleMediaChange}
              className="hidden"
            />
            {mediaPreview && (
              <img
                src={mediaPreview}
                alt="Preview"
                className="max-w-full h-48 rounded-lg border border-gray-600 mt-2"
              />
            )}
            <div className="flex justify-end mt-4">
              <button
                onClick={handlePostSubmit}
                className="bg-red-700 hover:bg-red-800 text-white font-semibold py-2 px-4 rounded-full"
              >
                Postar
              </button>
            </div>
          </div>
        );
      case "video":
        return (
          <div className="modal-content">
            <label
              htmlFor="mediaUpload"
              className="block text-white font-semibold mb-1 cursor-pointer"
            >
              Upload de Vídeo:
            </label>
            <input
              type="file"
              id="mediaUpload"
              accept="video/*"
              onChange={handleMediaChange}
              className="hidden"
            />
            {mediaPreview && (
              <video
                src={mediaPreview}
                controls
                className="max-w-full h-48 rounded-lg border border-gray-600 mt-2"
              ></video>
            )}
            <div className="flex justify-end mt-4">
              <button
                onClick={handlePostSubmit}
                className="bg-red-700 hover:bg-red-800 text-white font-semibold py-2 px-4 rounded-full"
              >
                Postar
              </button>
            </div>
          </div>
        );
      case "evento":
        return (
          <div className="modal-content">
            <input
              type="text"
              id="eventName"
              placeholder="Nome do Evento"
              className="w-full h-10 focus:ring-0 rounded-full pl-4 border border-gray-600 bg-gray-800 text-white mb-2"
            />
            <div className="mb-2">
              <label className="block text-white font-semibold mb-1">
                Data e Hora do Evento:
              </label>
              <DatePicker
                selected={eventDate}
                onChange={(date) => setEventDate(date)}
                showTimeSelect
                dateFormat="dd/MM/yyyy HH:mm"
                className="w-full h-10 focus:ring-0 rounded-full pl-4 border border-gray-600 bg-gray-800 text-white"
              />
            </div>
            <label
              htmlFor="mediaUpload"
              className="block text-white font-semibold mb-1 cursor-pointer"
            >
              Upload de Foto para o Evento:
            </label>
            <input
              type="file"
              id="mediaUpload"
              accept="image/*"
              onChange={handleMediaChange}
              className="hidden"
            />
            {mediaPreview && (
              <img
                src={mediaPreview}
                alt="Preview"
                className="max-w-full h-48 rounded-lg border border-gray-600 mt-2"
              />
            )}
            <div className="flex justify-end mt-4">
              <button
                onClick={handlePostSubmit}
                className="bg-red-700 hover:bg-red-800 text-white font-semibold py-2 px-4 rounded-full"
              >
                Postar
              </button>
            </div>
          </div>
        );
      case "meet":
        return (
          <div className="modal-content">
            <input
              type="text"
              id="meetTitle"
              placeholder="Título do Meet"
              className="w-full h-10 focus:ring-0 rounded-full pl-4 border border-gray-600 bg-gray-800 text-white mb-2"
            />
            <input
              type="text"
              id="meetLink"
              placeholder="Link da Sala"
              className="w-full h-10 focus:ring-0 rounded-full pl-4 border border-gray-600 bg-gray-800 text-white mb-2"
            />
            <textarea
              placeholder="Descrição"
              value={postText}
              onChange={(e) => setPostText(e.target.value)}
              className="w-full h-20 focus:ring-0 rounded-md pl-4 border border-gray-600 bg-gray-800 text-white mb-2"
            />
            <div className="flex justify-end mt-4">
              <button
                onClick={handlePostSubmit}
                className="bg-red-700 hover:bg-red-800 text-white font-semibold py-2 px-4 rounded-full"
              >
                Postar
              </button>
            </div>
          </div>
        );
      case "startup":
        return (
          <div className="modal-content">
            <input
              type="text"
              id="startupName"
              placeholder="Nome da Startup"
              className="w-full h-10 focus:ring-0 rounded-full pl-4 border border-gray-600 bg-gray-800 text-white mb-2"
            />
            <textarea
              placeholder="Descrição"
              value={postText}
              onChange={(e) => setPostText(e.target.value)}
              className="w-full h-20 focus:ring-0 rounded-md pl-4 border border-gray-600 bg-gray-800 text-white mb-2"
            />
            <div className="flex justify-end mt-4">
              <button
                onClick={handlePostSubmit}
                className="bg-red-700 hover:bg-red-800 text-white font-semibold py-2 px-4 rounded-full"
              >
                Postar
              </button>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="bg-gray-900 border border-gray-700 rounded-md p-4 mb-4">
      <div className="flex items-center mb-4 bg-gray-900">
        <input
          type="text"
          placeholder="Crie uma publicação"
          value={postText}
          onChange={(e) => setPostText(e.target.value)}
          className="w-full h-10 focus:ring-0 rounded-full pl-4 border border-gray-600 bg-gray-800 text-white"
        />
        <button
          onClick={() => setModalOpen(true)}
          className="ml-2 bg-red-700 hover:bg-red-800 text-white text-sm py-2 px-2 rounded-full"
        >
          Postar
        </button>
      </div>
      <div className="page-home-uploads-section flex justify-between text-white font-semibold">
        <button
          onClick={() => handlePostTypeChange("photo")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-yellow-600 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaCamera className="text-white" />
          </span>
          Foto
        </button>
        <button
          onClick={() => handlePostTypeChange("video")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-yellow-600 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaVideo className="text-white" />
          </span>
          Vídeo
        </button>
        <button
          onClick={() => handlePostTypeChange("evento")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-yellow-600 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaCalendar className="text-white" />
          </span>
          Evento
        </button>
        <button
          onClick={() => handlePostTypeChange("meet")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-yellow-600 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaMeetup className="text-white" />
          </span>
          Meet
        </button>
        <button
          onClick={() => handlePostTypeChange("startup")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-yellow-600 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaCamera className="text-white" />
          </span>
          Startup
        </button>
      </div>

      {modalOpen && (
        <div className="modal fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="modal-content bg-gray-800 p-4 rounded-lg w-full max-w-lg">
            <button
              onClick={() => setModalOpen(false)}
              className="absolute top-2 right-2 text-gray-400 hover:text-white"
            >
              &times;
            </button>
            {renderModalContent()}
          </div>
        </div>
      )}
    </div>
  );
}
