import React, { useEffect, useState } from 'react';
import { PrincipalPage } from '../../layout/principalPage';
import "./userpage.css";
import Carex1 from "../../assets/img/carex.png";
import CarexLogo from "../../assets/img/carex_logo.png";
import { CreatePost } from '../../components/CreatePost/CreatePost';
import { MainPost } from '../../components/MainPost/MainPost';
import { Link } from 'react-router-dom';
import aguardar from '../../assets/img/Spin@1x-1.0s-200px-200px (1).gif';

export function UserPage() {
    const [userData, setUserData] = useState(null);
    const [activeFilter, setActiveFilter] = useState('todos'); 

    useEffect(() => {
        const fetchUserData = () => {
            setTimeout(() => {
                const dummyData = {
                    company: {
                        nome_empresa: 'Nome da Empresa',
                    },
                    entrepreneur: {
                        nome: 'Nome do Empreendedor',
                    },
                    title: 'Título do Usuário',
                    area_atuacao: 'Área de Atuação',
                };
                setUserData(dummyData);
            }, 1000);
        };

        fetchUserData();
    }, []);

    if (!userData) {
        return (
            <div className="loading-container flex justify-center items-center h-screen">
                <img src={aguardar} alt="Loading..." className="loading-gif" />
            </div>
        );
    }

    const { company, entrepreneur } = userData;
    const displayName = company ? company.nome_empresa : (entrepreneur ? entrepreneur.nome : 'Nome não disponível');

    const handleFilterChange = (filter) => {
        setActiveFilter(filter);
    };

    return (
        <PrincipalPage>
            <div className="home-page-cards flex flex-col lg:flex-row justify-between rounded-md h-screen"> {/* Adiciona overflow-y-auto e overflow-x-hidden */}
                <div className="home-page-col-one bg-gray-800 rounded-md fixed top-0 left-0 hidden lg:block w-full lg:w-1/5 h-screen"> {/* Adiciona fixed, top-0, left-0 e h-screen */}
                    {/* ...  Conteúdo da coluna 1  ... */}
                </div>

                <div className="home-page-col-two w-full lg:w-3/5 bg-gray-800 rounded-md overflow-y-auto p-4"> {/* Adiciona overflow-y-auto e p-4 */}
                    <CreatePost />
                    <MainPost filter={activeFilter} /> 
                </div>
                <div className="home-page-col-three w-full lg:w-1/5 bg-gray-800 rounded-md mt-4 lg:mt-0 p-4 fixed top-0 right-0 hidden lg:block h-screen"> {/* Adiciona fixed, top-0, right-0, e h-screen */}
                    {/* ...  Conteúdo da coluna 3  ... */}
                </div>
            </div>
        </PrincipalPage>
    );
}
