import React, { useState } from 'react';
import { Dialog, Transition } from '@headlessui/react';
import { useNavigate } from 'react-router-dom';
import areasJSON from '../../JSON/areas.json';
import provinciasJSON from '../../JSON/provincias.json';
import biJSON from '../../JSON/bi.json';
import "./Register.css";
import axios from 'axios';

// Extrair os dados dos arquivos JSON
const areas = areasJSON.areas; 
const provincias = provinciasJSON.provincias; 
const validBIs = biJSON.bis; 

export function FormularioEmpreendedor() {
  const [formData, setFormData] = useState({
    Email: '',
    Senha: '',
    Telefone: '',
    Provincia: '',
    AreaAtuacao: '',
    BI: '',
    Nome: '',
    DataNascimento: '',
    Genero: '',
    Endereco: ''
  });

  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState(null);
  const navigate = useNavigate();

  const handleVoltar = () => {
    window.history.back(); // Isso volta para a página anterior no histórico de navegação
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validação do BI
    if (!validBIs.includes(formData.BI)) {
      setMessage({ type: 'error', text: 'BI inválido!' });
      setIsOpen(true);
      return; 
    }

    // Validação dos outros campos
    if (!formData.Email || !formData.Senha || !formData.Telefone || !formData.Provincia || !formData.AreaAtuacao || !formData.BI || !formData.Nome || !formData.DataNascimento || !formData.Genero || !formData.Endereco) {
      setMessage({ type: 'error', text: 'Por favor, preencha todos os campos.' });
      setIsOpen(true);
      return;
    }

    // Validação do email
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.Email)) {
      setMessage({ type: 'error', text: 'Email inválido.' });
      setIsOpen(true);
      return;
    }

    // Validação do telefone
    if (!/^[0-9]{9}$/.test(formData.Telefone)) {
      setMessage({ type: 'error', text: 'Número de telefone inválido. Deve ter 9 dígitos.' });
      setIsOpen(true);
      return;
    }

    // Envia os dados para a API
    axios.post('http://localhost:3001/register', JSON.stringify(formData), {
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(response => {
      // Simulação de sucesso de registro
      setMessage({ type: 'success', text: 'Registro bem-sucedido!' });
      setIsOpen(true);
    })
    .catch(error => {
      // Trata o erro da API
      if (error.response && error.response.status === 400) {
        setMessage({ type: 'error', text: error.response.data.message || 'Erro na validação dos dados.' });
      } else {
        setMessage({ type: 'error', text: 'Erro ao registrar. Tente novamente mais tarde.' });
      }
      setIsOpen(true);
    });
  };

  const closeModal = () => {
    setIsOpen(false);
    // Redireciona para a página de login apenas se o cadastro for bem-sucedido
    if (message && message.type === 'success') {
      navigate('/login');
      // Limpa os campos do formulário
      setFormData({
        Email: '',
        Senha: '',
        Telefone: '',
        Provincia: '',
        AreaAtuacao: '',
        BI: '',
        Nome: '',
        DataNascimento: '',
        Genero: '',
        Endereco: ''
      });
    }
  };

  return (
    <div className="bg-gray-900 p-8 rounded-lg shadow-lg w-full max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold text-white mb-3">Registro de Empreendedor</h2>
      <form onSubmit={handleSubmit} className="grid-cols-1">
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
          <div className='col-span-2'>
            <label className="block text-gray-400 mb-2">Nome</label>
            <input
              type="text"
              name="Nome"
              className="capitalize w-full p-2 rounded bg-gray-700 text-white border-b-2 focus:ring-transparent"
              value={formData.Nome}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Email</label>
            <input
              type="email"
              name="Email"
              className="lowercase w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.Email}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Senha</label>
            <input
              type="password"
              name="Senha"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.Senha}
              onChange={handleChange}
              required
              minLength={8}
              pattern="^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,20}$"
              title="Sua senha deve ter pelo menos 8 caracteres, uma letra maiúscula, uma letra minúscula, um número e um caractere especial."
            />
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Telefone</label>
            <input
              type="text"
              name="Telefone"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.Telefone}
              onChange={handleChange}
              required
              minLength={9}
              maxLength={9}
              pattern="[0-9]{9}"
            />
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Província</label>
            <select
              name="Provincia"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.Provincia}
              onChange={handleChange}
              required
            >
              <option value="">Selecione</option>
              {provincias.map((provincia) => (
                <option key={provincia} value={provincia}>{provincia}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Área de Atuação</label>
            <select
              name="AreaAtuacao"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.AreaAtuacao}
              onChange={handleChange}
              required
            >
              <option value="">Selecione</option>
              {areas.map((area) => (
                <option key={area} value={area}>{area}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-gray-400 mb-2">BI</label>
            <input
              type="text"
              name="BI"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.BI}
              onChange={handleChange}
              required
              minLength={14}
              maxLength={14}
              pattern="^\d{9}[A-Z]{2}\d{3}$"
            />
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Data de Nascimento</label>
            <input
              type="date"
              name="DataNascimento"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.DataNascimento}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Gênero</label>
            <select
              name="Genero"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.Genero}
              onChange={handleChange}
              required
            >
              <option value="">Selecione</option>
              <option value="Masculino">Masculino</option>
              <option value="Feminino">Feminino</option>
              <option value="Outro">Outro</option>
            </select>
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Endereço</label>
            <input
              type="text"
              name="Endereco"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.Endereco}
              onChange={handleChange}
              required
            />
          </div>
        </div>
        <div className='flex justify-end'>
          <div className='mr-3'>
            <button onClick={handleVoltar} className="col-span-1 mt-4 bg-gray-700 hover:bg-gray-800 text-white font-bold py-2 px-4 rounded">
              Cancelar
            </button>
          </div>
          <div>
            <button type="submit" className="col-span-1 mt-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
              Registrar
            </button>
          </div>
        </div>
      </form>

      <Transition appear show={isOpen} as={React.Fragment}>
        <Dialog
          as="div"
          className="fixed inset-0 z-10 overflow-y-auto"
          onClose={closeModal}
        >
          <div className="min-h-screen px-4 text-center">
            <Transition.Child
              as={React.Fragment}
              enter="ease-out duration-900"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="ease-in duration-900"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <div className="fixed inset-0 bg-black bg-opacity-50" />
            </Transition.Child>

            <span className="inline-block h-screen align-middle" aria-hidden="true">
              ​
            </span>
            <Transition.Child
              as={React.Fragment}
              enter="ease-out duration-900"
              enterFrom="opacity-0 translate-y-4"
              enterTo="opacity-100 translate-y-0"
              leave="ease-in duration-900"
              leaveFrom="opacity-100 translate-y-0"
              leaveTo="opacity-0 translate-y-4"
            >
              <div className="inline-block w-full max-w-md p-6 my-8 overflow-hidden text-left align-middle transition-all transform bg-white shadow-xl rounded-2xl">
                <Dialog.Title as="h3" className="text-lg font-medium leading-6 text-gray-900">Informação</Dialog.Title>
                <div className="mt-2">
                  <p className="text-sm text-gray-500">{message ? message.text : ''}</p>
                </div>

                <div className="mt-4">
                  <button
                    type="button"
                    className="inline-flex justify-center px-4 py-2 text-sm font-medium text-white bg-blue-500 border border-transparent rounded-md hover:bg-blue-600 focus:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-blue-500"
                    onClick={closeModal}
                  >
                    Fechar
                  </button>
                </div>
              </div>
            </Transition.Child>
          </div>
        </Dialog>
      </Transition>
    </div>
  );
}

export default FormularioEmpreendedor;
