import React from 'react';
import TimelineHeader from "../../components/TimelineHeader/TimelineHeader";
import { PrincipalPage } from '../../layout/principalPage';
import { Intro } from '../../components/Intro/intro';
import { CreatePost } from '../../components/CreatePost/CreatePost';
import { MainPost } from '../../components/MainPost/MainPost';
import { ConnectionsPage } from "../../pages/ConnectioPage";
import { ServicesPage } from "../../pages/ServicePage";
import { UserDashboard } from '../UserDashboard';

export function UserProfile() {
  const [activeFilter, setActiveFilter] = useState('todos');

  const handleFilterChange = (filter) => {
    setActiveFilter(filter);}

  return (
    <PrincipalPage>
      <TimelineHeader />
      <div className="grid grid-cols-1 lg:grid-cols-12 pt-4 gap-4 z-0 pb-56">
        <div className="col-span-1 lg:col-span-4 lg:col-start-1 space-y-4">
          <Intro />
        </div>
        <div className="col-span-1 lg:col-span-8 lg:col-start-5 space-y-4">
          {/* Renderizar os componentes de acordo com a URL */}
          <CreatePost /> 

          <div className="home-page-col-three w-full border border-gray-700 lg:w-1/5 bg-gray-800 rounded-md mt-4 lg:mt-0 p-4 hidden lg:block h-screen">
    <h4 className="text-white text-lg font-medium mb-4">Filtrar Posts</h4>
    <div className="flex flex-col gap-2">
        <button
            className={`bg-gray-700 text-white px-4 py-2 rounded-md hover:bg-gray-600 ${activeFilter === 'todos' ? 'bg-blue-500' : ''}`}
            onClick={() => handleFilterChange('todos')}
        >
            Todos
        </button>
        <button
            className={`bg-gray-700 text-white px-4 py-2 rounded-md hover:bg-gray-600 ${activeFilter === 'eventos' ? 'bg-blue-500' : ''}`}
            onClick={() => handleFilterChange('eventos')}
        >
            Eventos
        </button>
        <button
            className={`bg-gray-700 text-white px-4 py-2 rounded-md hover:bg-gray-600 ${activeFilter === 'startup' ? 'bg-blue-500' : ''}`}
            onClick={() => handleFilterChange('startup')}
        >
            Startup
        </button>
        <button
            className={`bg-gray-700 text-white px-4 py-2 rounded-md hover:bg-gray-600 ${activeFilter === 'salasDeNegocios' ? 'bg-blue-500' : ''}`}
            onClick={() => handleFilterChange('salasDeNegocios')}
        >
            Salas de Neg√≥cios
        </button>
    </div>

</div>
          {window.location.pathname === '/connections' && <ConnectionsPage />}
          {window.location.pathname === '/views' && <UserDashboard />}
          {window.location.pathname === '/services' && <ServicesPage />}

          <MainPost filter={activeFilter} /> 
        </div>
      </div>
    </PrincipalPage>
  );
}
