import React, { useState, useEffect } from 'react';
import { FaThumbsUp, FaShare, FaChevronCircleRight } from 'react-icons/fa';

export const MainPost = ({ postagem }) => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Função para buscar os dados do usuário a partir da API
    const fetchUserData = async () => {
      try {
        const response = await fetch(`http://localhost:3001/register/me/${postagem.UsuarioID}`); // Substitua pela URL correta da sua API
        const data = await response.json();
        setUserData(data);
      } catch (error) {
        console.error('Erro ao obter os dados do usuário:', error);
      }
    };

    fetchUserData();
  }, [postagem.UsuarioID]); // Adiciona postagem.UsuarioID como dependência

  // Função para obter o nome e a foto do autor com base no tipo (empresa ou empreendedor)
  const getAuthorData = () => {
    if (userData && userData.Tipo === 'empresa') {
      return {
        name: userData.Empresa.NomeEmpresa,
        photo: userData.Empresa.Foto || 'https://ui-avatars.com/api/?name=Empresa&size=40',
      };
    } else if (userData && userData.Tipo === 'empreendedor') {
      return {
        name: userData.Empreendedor.Nome,
        photo: userData.Empreendedor.Foto || 'https://ui-avatars.com/api/?name=Empreendedor&size=40',
      };
    } else {
      return {
        name: 'Nome não disponível',
        photo: 'https://ui-avatars.com/api/?name=Usuário&size=40', // Foto padrão caso o tipo seja inválido
      };
    }
  };

  // Obtendo o nome e a foto do autor
  const { name, photo } = getAuthorData();

  // Verifica se o userData já foi carregado
  if (!userData) {
    return (
      <div className="loading-container flex justify-center items-center h-screen">
        <p>Carregando...</p>
      </div>
    );
  }

  return (
    <div className="containerPost bg-gray-900 border border-gray-700 rounded-md p-4">
      <div className="postagem p-4 rounded-lg border border-gray-200 mb-4">
        <div className="flex items-center gap-4">
          <div className="avatar">
            <img src={photo} alt="Foto do autor" className="rounded-full" />
          </div>
          <div className="autor font-medium text-gray-800">
            {name} - {postagem.AreaAtuacao} - {postagem.Data} 
          </div>
        </div>
        <div className="conteudo text-gray-200 mt-2">
          {postagem.Conteudo}
        </div>
        {postagem.Foto && (
          <img src={postagem.Foto} alt="Foto da postagem" className="w-full rounded-lg mt-4" />
        )}
        <div className="flex justify-evenly items-center mb-4">
          <button className="flex items-center text-blue-400"><FaThumbsUp className="mr-2" /> Curtir</button>
          <button className="flex items-center text-blue-400"><FaShare className="mr-2" /> Compartilhar</button>
        </div>
        <div className="bg-gray-800 rounded-md p-2 mb-2 flex justify-between">
          <input type="text" placeholder='Comentar...' className="w-full h-10 rounded-full pl-4 border border-gray-600 bg-gray-600 text-white" />
          <button className='text-blue-400 text-3xl ml-2'><FaChevronCircleRight /></button>
        </div>
      </div>
    </div>
  );
};
