                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'midias':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Mídias</h2>
            <textarea
              placeholder="Descrição da Mídia"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="create-post-container">
      <div className="header">
        <img src={CarexLogo} alt="Logo" className="logo" />
        <h1 className="title">Criar Nova Postagem</h1>
      </div>
      <div className="post-options">
        <button onClick={() => handlePostTypeChange('meet')} className="option-button">
          <FaCalendar size={30} />
          <p>Meet</p>
        </button>
        <button onClick={() => handlePostTypeChange('startup')} className="option-button">
          <FaLine size={30} />
          <p>StartUp</p>
        </button>
        <button onClick={() => handlePostTypeChange('evento')} className="option-button">
          <FaVideo size={30} />
          <p>Evento</p>
        </button>
        <button onClick={() => handlePostTypeChange('midias')} className="option-button">
          <FaCamera size={30} />
          <p>Mídias</p>
        </button>
      </div>
      {modalOpen && (
        <div className="modal">
          <div className="modal-overlay" onClick={() => setModalOpen(false)}></div>
          <div className="modal-content">
            {renderModalContent()}
          </div>
        </div>
      )}
    </div>
  );
}
