import React, { useState } from 'react';

export function CreatePost() {
  const [showModal, setShowModal] = useState(false);
  const [postContent, setPostContent] = useState('');
  const [postImage, setPostImage] = useState(null);
  const [isPublishButtonDisabled, setIsPublishButtonDisabled] = useState(true); // Estado para desabilitar o botão "Publicar"

  const handleChangeContent = (event) => {
    setPostContent(event.target.value);
    setIsPublishButtonDisabled(!(postContent.trim() || postImage)); // Habilita o botão se tiver conteúdo ou imagem
  };

  const handleChangeImage = (event) => {
    setPostImage(event.target.files[0]);
    setIsPublishButtonDisabled(!(postContent.trim() || postImage)); // Habilita o botão se tiver conteúdo ou imagem
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Aqui você faria a requisição para o seu backend para salvar o post
    // console.log('Post enviado com sucesso:', postContent, postImage);

    // Limpar o textarea e a imagem
    setPostContent('');
    setPostImage(null);
    handleCloseModal();
  };

  // Função para fechar o modal ao clicar fora dele
  const handleClickOutside = (event) => {
    if (showModal && !event.target.closest('.modal-container')) {
      handleCloseModal();
    }
  };

  // Adicionar ouvinte de evento para clicar fora do modal
  React.useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [showModal]);

  return (
    <div>
      {/* Input que abre o modal */}
      <input
        type="text"
        placeholder="O que você está pensando?"
        className="w-full px-3 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500"
        onClick={handleOpenModal}
      />

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 z-10 overflow-y-auto flex items-center justify-center modal-container">
          {/* Overlay atrás do modal */}
          <div className="fixed inset-0 bg-black bg-opacity-50 pointer-events-none"></div>

          <div className="bg-white rounded-lg shadow-md w-11/12 max-w-lg px-4 pt-5 pb-4 text-left overflow-hidden sm:p-6 sm:w-10/12 lg:w-8/12">
            <div className="flex items-center gap-2 mb-4">
              <img src="/user.png" alt="User avatar" className="w-10 h-10 rounded-full" />
              <div className="flex-1">
                <h3 className="text-lg font-medium text-gray-900">
                  Nvuola Carvalho
                </h3>
                <span className="text-gray-500">
                  Publicar em Todos
                </span>
                <div className="mt-3">
                  <textarea
                    rows={4}
                    placeholder="Sobre o que você quer falar?"
                    className="w-full resize-none rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 p-2"
                    value={postContent}
                    onChange={handleChangeContent}
                  />
                </div>
              </div>
            </div>
            <div className="flex mt-4 gap-4">
              <button type="button" className="flex items-center gap-2 bg-gray-200 text-gray-600 px-4 py-2 rounded-lg hover:bg-gray-300">
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l.879.879a2 2 0 010 2.828l-.879.879m-4 0l.879.879a2 2 0 010 2.828l-.879.879m-6 0l.879.879a2 2 0 010 2.828l-.879.879" /></svg>
                Foto
              </button>
              <button type="button" className="flex items-center gap-2 bg-gray-200 text-gray-600 px-4 py-2 rounded-lg hover:bg-gray-300">
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a2 2 0 012-2h1a2 2 0 012 2v3.158c.598.087.828.44.584.905L16 17m-5-4h5V5a2 2 0 00-2-2h-1a2 2 0 00-2 2v6m4 0v6a2 2 0 01-2 2h-1a2 2 0 01-2-2V5m5 12v.01" /></svg>
                Vídeo
              </button>
              <button type="button" className="flex items-center gap-2 bg-gray-200 text-gray-600 px-4 py-2 rounded-lg hover:bg-gray-300">
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
                Artigo
              </button>
            </div>
            <form onSubmit={handleSubmit}>
              <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 mt-4" disabled={isPublishButtonDisabled}>
                Publicar
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
