import React, { useState } from 'react';

export function CreatePost() {
  const [showModal, setShowModal] = useState(false);
  const [activeTab, setActiveTab] = useState('post'); // Estado para controlar a aba ativa
  const [postContent, setPostContent] = useState('');
  const [postMedia, setPostMedia] = useState(null);
  const [isPublishButtonDisabled, setIsPublishButtonDisabled] = useState(true);
  const [startupName, setStartupName] = useState('');
  const [startupDescription, setStartupDescription] = useState('');
  const [zoomLink, setZoomLink] = useState('');
  const [businessRoomTitle, setBusinessRoomTitle] = useState('');
  const [businessRoomDescription, setBusinessRoomDescription] = useState('');
  const [eventName, setEventName] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [eventTime, setEventTime] = useState('');
  const [eventLocation, setEventLocation] = useState('');
  const [eventDescription, setEventDescription] = useState('');

  const handleChangeContent = (event) => {
    setPostContent(event.target.value);
    setIsPublishButtonDisabled(!(postContent.trim() || postMedia));
  };

  const handleChangeMedia = (event) => {
    setPostMedia(event.target.files[0]);
    setIsPublishButtonDisabled(!(postContent.trim() || postMedia));
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Aqui você faria a requisição para o seu backend para salvar o post
    // console.log('Post enviado com sucesso:', postContent, postMedia);

    // Limpar os estados
    setPostContent('');
    setPostMedia(null);
    setStartupName('');
    setStartupDescription('');
    setZoomLink('');
    setBusinessRoomTitle('');
    setBusinessRoomDescription('');
    setEventName('');
    setEventDate('');
    setEventTime('');
    setEventLocation('');
    setEventDescription('');
    handleCloseModal();
  };

  const handleClickOutside = (event) => {
    if (showModal && !event.target.closest('.modal-container')) {
      handleCloseModal();
    }
  };

  React.useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [showModal]);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="containerPost bg-gray-900 border border-gray-700 rounded-md p-4">
    <div>
      {/* Botão para abrir o modal */}
      <input
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        onClick={handleOpenModal}
        placeholder='O que estás a pensar?'
     />


      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 z-10 overflow-y-auto flex items-center justify-center modal-container">

          <div className="bg-white rounded-lg shadow-md w-11/12 max-w-lg px-4 pt-5 pb-4 text-left overflow-hidden sm:p-6 sm:w-10/12 lg:w-8/12">
            <div className="flex items-center gap-2 mb-4">
              <img src="/user.png" alt="User avatar" className="w-10 h-10 rounded-full" />
              <div className="flex-1">
                <h3 className="text-lg font-medium text-gray-900">
                  Nvuola Carvalho
                </h3>
                <span className="text-gray-500">
                  Publicar em Todos
                </span>
              </div>
            </div>

            {/* Abas de Conteúdo */}
            <div className="flex mb-4">
              <button
                className={`px-4 py-2 rounded-t-lg ${
                  activeTab === 'post' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-600'
                }`}
                onClick={() => handleTabChange('post')}
              >
                Postagem
              </button>
              <button
                className={`px-4 py-2 rounded-t-lg ${
                  activeTab === 'startup' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-600'
                }`}
                onClick={() => handleTabChange('startup')}
              >
                Startup
              </button>
              <button
                className={`px-4 py-2 rounded-t-lg ${
                  activeTab === 'businessRoom' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-600'
                }`}
                onClick={() => handleTabChange('businessRoom')}
              >
                Sala de Negócios
              </button>
              <button
                className={`px-4 py-2 rounded-t-lg ${
                  activeTab === 'event' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-600'
                }`}
                onClick={() => handleTabChange('event')}
              >
                Evento
              </button>
            </div>

            {/* Conteúdo da Aba Ativa */}
            {activeTab === 'post' && (
              <div>
                <div className="mt-3">
                  <textarea
                    rows={4}
                    placeholder="Sobre o que você quer falar?"
                    className="w-full resize-none rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 p-2"
                    value={postContent}
                    onChange={handleChangeContent}
                  />
                </div>
                <div className="flex mt-4 gap-4">
                  <div>
                    <label htmlFor="postMedia" className="text-gray-600 block mb-1 font-medium">Imagem/Vídeo:</label>
                    <input
                      type="file"
                      id="postMedia"
                      accept="image/*, video/*"
                      onChange={handleChangeMedia}
                      className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:ring-2 focus:ring-blue-500"
                    />
                    {postMedia && (
                      <div className="mt-2">
                        {postMedia.type.startsWith('image/') ? (
                          <img src={URL.createObjectURL(postMedia)} alt="Preview da imagem" className="w-24 h-24 mt-2 rounded-lg" />
                        ) : (
                          <video src={URL.createObjectURL(postMedia)} controls className="w-24 h-24 mt-2 rounded-lg" />
                        )}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'startup' && (
              <div>
                <div className="mt-3">
                  <label htmlFor="startupName" className="block mb-2 text-gray-700 text-sm font-bold">Nome da Startup:</label>
                  <input
                    type="text"
                    id="startupName"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={startupName}
                    onChange={(e) => setStartupName(e.target.value)}
                  />
                </div>
                <div className="mt-3">
                  <label htmlFor="startupDescription" className="block mb-2 text-gray-700 text-sm font-bold">Descrição:</label>
                  <textarea
                    id="startupDescription"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={startupDescription}
                    onChange={(e) => setStartupDescription(e.target.value)}
                  />
                </div>
                <div className="mt-3">
                  <label htmlFor="postMedia" className="text-gray-600 block mb-1 font-medium">Imagem/Vídeo:</label>
                  <input
                    type="file"
                    id="postMedia"
                    accept="image/*, video/*"
                    onChange={handleChangeMedia}
                    className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:ring-2 focus:ring-blue-500"
                  />
                  {postMedia && (
                    <div className="mt-2">
                      {postMedia.type.startsWith('image/') ? (
                        <img src={URL.createObjectURL(postMedia)} alt="Preview da imagem" className="w-24 h-24 mt-2 rounded-lg" />
                      ) : (
                        <video src={URL.createObjectURL(postMedia)} controls className="w-24 h-24 mt-2 rounded-lg" />
                      )}
                    </div>
                  )}
                </div>
              </div>
            )}

            {activeTab === 'businessRoom' && (
              <div>
                <div className="mt-3">
                  <label htmlFor="businessRoomTitle" className="block mb-2 text-gray-700 text-sm font-bold">Título da Sala:</label>
                  <input
                    type="text"
                    id="businessRoomTitle"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={businessRoomTitle}
                    onChange={(e) => setBusinessRoomTitle(e.target.value)}
                  />
                </div>
                <div className="mt-3">
                  <label htmlFor="zoomLink" className="block mb-2 text-gray-700 text-sm font-bold">Link da Sala no Zoom:</label>
                  <input
                    type="text"
                    id="zoomLink"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={zoomLink}
                    onChange={(e) => setZoomLink(e.target.value)}
                  />
                </div>
                <div className="mt-3">
                  <label htmlFor="businessRoomDescription" className="block mb-2 text-gray-700 text-sm font-bold">Descrição:</label>
                  <textarea
                    id="businessRoomDescription"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={businessRoomDescription}
                    onChange={(e) => setBusinessRoomDescription(e.target.value)}
                  />
                </div>
                <div className="mt-3">
                  <label htmlFor="postMedia" className="text-gray-600 block mb-1 font-medium">Imagem/Vídeo:</label>
                  <input
                    type="file"
                    id="postMedia"
                    accept="image/*, video/*"
                    onChange={handleChangeMedia}
                    className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:ring-2 focus:ring-blue-500"
                  />
                  {postMedia && (
                    <div className="mt-2">
                      {postMedia.type.startsWith('image/') ? (
                        <img src={URL.createObjectURL(postMedia)} alt="Preview da imagem" className="w-24 h-24 mt-2 rounded-lg" />
                      ) : (
                        <video src={URL.createObjectURL(postMedia)} controls className="w-24 h-24 mt-2 rounded-lg" />
                      )}
                    </div>
                  )}
                </div>
              </div>
            )}

            {activeTab === 'event' && (
              <div>
                <div className="mt-3">
                  <label htmlFor="eventName" className="block mb-2 text-gray-700 text-sm font-bold">Nome do Evento:</label>
                  <input
                    type="text"
                    id="eventName"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={eventName}
                    onChange={(e) => setEventName(e.target.value)}
                  />
                </div>
                <div className="mt-3">
                  <label htmlFor="eventDate" className="block mb-2 text-gray-700 text-sm font-bold">Data do Evento:</label>
                  <input
                    type="date"
                    id="eventDate"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={eventDate}
                    onChange={(e) => setEventDate(e.target.value)}
                  />
                </div>
                <div className="mt-3">
                  <label htmlFor="eventTime" className="block mb-2 text-gray-700 text-sm font-bold">Hora do Evento:</label>
                  <input
                    type="time"
                    id="eventTime"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={eventTime}
                    onChange={(e) => setEventTime(e.target.value)}
                  />
                </div>
                <div className="mt-3">
                  <label htmlFor="eventLocation" className="block mb-2 text-gray-700 text-sm font-bold">Local do Evento:</label>
                  <input
                    type="text"
                    id="eventLocation"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={eventLocation}
                    onChange={(e) => setEventLocation(e.target.value)}
                  />
                </div>
                <div className="mt-3">
                  <label htmlFor="eventDescription" className="block mb-2 text-gray-700 text-sm font-bold">Descrição do Evento:</label>
                  <textarea
                    id="eventDescription"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={eventDescription}
                    onChange={(e) => setEventDescription(e.target.value)}
                  />
                </div>
                <div className="mt-3">
                  <label htmlFor="postMedia" className="text-gray-600 block mb-1 font-medium">Imagem/Vídeo:</label>
                  <input
                    type="file"
                    id="postMedia"
                    accept="image/*, video/*"
                    onChange={handleChangeMedia}
                    className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:ring-2 focus:ring-blue-500"
                  />
                  {postMedia && (
                    <div className="mt-2">
                      {postMedia.type.startsWith('image/') ? (
                        <img src={URL.createObjectURL(postMedia)} alt="Preview da imagem" className="w-24 h-24 mt-2 rounded-lg" />
                      ) : (
                        <video src={URL.createObjectURL(postMedia)} controls className="w-24 h-24 mt-2 rounded-lg" />
                      )}
                    </div>
                  )}
                </div>
              </div>
            )}

            <form onSubmit={handleSubmit} className="flex justify-end mt-4">
              <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600" disabled={isPublishButtonDisabled}>
                Publicar
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
    </div>
  );
}
