import React, { useState } from 'react';

function CreatePost() {
  const [postContent, setPostContent] = useState('');
  const [postImage, setPostImage] = useState(null);
  const [postFiles, setPostFiles] = useState([]);
  const [postPreview, setPostPreview] = useState(null);

  const handleChangeContent = (event) => {
    setPostContent(event.target.value);
  };

  const handleChangeImage = (event) => {
    setPostImage(event.target.files[0]);
    if (event.target.files[0]) {
      const reader = new FileReader();
      reader.onload = (e) => setPostPreview(e.target.result);
      reader.readAsDataURL(event.target.files[0]);
    }
  };

  const handleChangeFiles = (event) => {
    setPostFiles(Array.from(event.target.files));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Aqui você faria a requisição para o seu backend para salvar o post
    // console.log('Post enviado com sucesso:', postContent, postImage, postFiles); 

    // Limpar os campos do formulário
    setPostContent('');
    setPostImage(null);
    setPostFiles([]);
    setPostPreview(null);
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-4 w-full max-w-md mx-auto">
      <div className="flex items-center gap-2 mb-4">
        <img src="/user.png" alt="User avatar" className="w-10 h-10 rounded-full" />
        <div className="flex-1">
          <textarea
            rows={4}
            placeholder="O que você está pensando?"
            className="w-full resize-none rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 p-2"
            value={postContent}
            onChange={handleChangeContent}
          />
        </div>
      </div>
      <div className="flex mt-4 gap-4">
        <div className="flex items-center gap-2">
          <button type="button" className="flex items-center gap-2 bg-gray-200 text-gray-600 px-4 py-2 rounded-lg hover:bg-gray-300">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l.879.879a2 2 0 010 2.828l-.879.879m-4 0l.879.879a2 2 0 010 2.828l-.879.879m-6 0l.879.879a2 2 0 010 2.828l-.879.879" /></svg>
            Foto
          </button>
        </div>
        <div className="flex items-center gap-2">
          <button type="button" className="flex items-center gap-2 bg-gray-200 text-gray-600 px-4 py-2 rounded-lg hover:bg-gray-300">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a2 2 0 012-2h1a2 2 0 012 2v3.158c.598.087.828.44.584.905L16 17m-5-4h5V5a2 2 0 00-2-2h-1a2 2 0 00-2 2v6m4 0v6a2 2 0 01-2 2h-1a2 2 0 01-2-2V5m5 12v.01" /></svg>
            Vídeo
          </button>
        </div>
        <div className="flex items-center gap-2">
          <button type="button" className="flex items-center gap-2 bg-gray-200 text-gray-600 px-4 py-2 rounded-lg hover:bg-gray-300">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
            Artigo
          </button>
        </div>
      </div>
      <div className="flex mt-4 gap-4">
        <div>
          <label htmlFor="postImage" className="text-gray-600 block mb-1 font-medium">Imagem:</label>
          <input
            type="file"
            id="postImage"
            accept="image/*"
            onChange={handleChangeImage}
            className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
          {postPreview && (
            <img src={postPreview} alt="Preview da imagem" className="w-24 h-24 mt-2 rounded-lg" />
          )}
        </div>
        <div>
          <label htmlFor="postFiles" className="text-gray-600 block mb-1 font-medium">Arquivos:</label>
          <input
            type="file"
            id="postFiles"
            multiple
            onChange={handleChangeFiles}
            className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>
      </div>
      <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 mt-4">
        Publicar
      </button>
    </div>
  );
}

export default CreatePost;
