import React, { useState, useContext, useEffect } from 'react';
import { PrincipalPage } from '../../layout/principalPage';
import areasJSON from '../../JSON/areas.json';
import provinciasJSON from '../../JSON/provincias.json';
import CapaDefault from "../../assets/img/CapaDefoult.jpg";
import ProfileDefault from "../../assets/img//profileDefoult.png";
import { AuthContext } from '../../context/AuthContext'; // Importe o contexto de autenticação
import axios from 'axios';

const areas = areasJSON.areas; 
const provincias = provinciasJSON.provincias;



export const ProfileEdit = () => {
  // Estados para os dados do usuário
  const [email, setEmail] = useState('');
  const [telefone, setTelefone] = useState('');
  const [provincia, setProvincia] = useState('');
  const [areaAtuacao, setAreaAtuacao] = useState('');
  const [profileImage, setProfileImage] = useState(null);
  const [coverImage, setCoverImage] = useState(null);
  const [titleUser, setTitleUser] = useState('');
  const [nifEmpresa, setNifEmpresa] = useState('');
  const [nomeEmpresa, setNomeEmpresa] = useState('');
  const [nomeRepresentante, setNomeRepresentante] = useState('');
  const [anoExistencia, setAnoExistencia] = useState('');
  const [bi, setBi] = useState('');
  const [nomeEmpreendedor, setNomeEmpreendedor] = useState('');
  const [dataNascimento, setDataNascimento] = useState('');
  const [genero, setGenero] = useState('');
  const [successDialogOpen, setSuccessDialogOpen] = useState(false);
  const [errorDialogOpen, setErrorDialogOpen] = useState(false);
  const [dialogMessage, setDialogMessage] = useState('');
  const [editing, setEditing] = useState(false);
  const { user, userType, token } = useContext(AuthContext); // Obtenha o usuário e o tipo de usuário do contexto

  // Carrega os dados do usuário quando o componente é montado
  useEffect(() => {
    if (user) {
      // Preenche os estados com os dados do usuário
      setEmail(user.Email);
      setTelefone(user.Telefone);
      setProvincia(user.Provincia);
      setAreaAtuacao(user.AreaAtuacao);
      setTitleUser(user.tipo);

      // Preenche os estados específicos de acordo com o tipo de usuário
      if (userType === 'empresa') {
        setNifEmpresa(user.company.NIF);
        setNomeEmpresa(user.company.NomeEmpresa);
        setNomeRepresentante(user.company.NomeRepresentante);
        setAnoExistencia(user.company.AnosDeExistencia);
      } else if (userType === 'empreendedor') {
        setBi(user.entrepreneur.BI);
        setNomeEmpreendedor(user.entrepreneur.Nome);
        setDataNascimento(user.entrepreneur.DataNascimento);
        setGenero(user.entrepreneur.Genero);
      }
    }
  }, [user, userType]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      let updatedUser = {
        Email: email,
        Telefone: telefone,
        Provincia: provincia,
        AreaAtuacao: areaAtuacao,
        tipo: titleUser
      };

      // Adiciona dados específicos de acordo com o tipo de usuário
      if (userType === 'empresa') {
        updatedUser.NIF = nifEmpresa;
        updatedUser.NomeEmpresa = nomeEmpresa;
        updatedUser.NomeRepresentante = nomeRepresentante;
        updatedUser.AnosDeExistencia = anoExistencia;
      } else if (userType === 'empreendedor') {
        updatedUser.BI = bi;
        updatedUser.Nome = nomeEmpreendedor;
        updatedUser.dataNascimento = dataNascimento;
        updatedUser.Genero = genero;
      }

      // Faz a atualização na API
      const response = await axios.put(
        `http://localhost:3001/usuarios/${user.id}`,
        updatedUser,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          }
        }
      );

      // Atualiza o contexto com os novos dados
      const updatedUserFromAPI = response.data; // Obter dados do usuário atualizado da API
      const newUser = {
        ...user,
        ...updatedUserFromAPI, // Merge dos dados existentes com os atualizados
        company: updatedUserFromAPI.company || user.company, // Adiciona ou mantém os dados da empresa
        entrepreneur: updatedUserFromAPI.entrepreneur || user.entrepreneur // Adiciona ou mantém os dados do empreendedor
      };
      // Atualiza o estado do usuário no contexto
      setUser(newUser);

      // Mostra a mensagem de sucesso
      setSuccessDialogOpen(true);
      setDialogMessage('Perfil atualizado com sucesso!');
      setEditing(false);
    } catch (error) {
      setErrorDialogOpen(true);
      setDialogMessage('Erro ao atualizar perfil. Por favor, tente novamente.');
      console.error('Erro ao atualizar perfil: ', error);
    }
  };

  // ... (restante do código para os eventos do SuccessDialog e ErrorDialog)

  // ... (restante do código para handleCancelClick e handleImageChange)

  return (
    <PrincipalPage>
      <div className="container mx-auto p-4">
        <h1 className="text-2xl font-semibold mb-4">Editar Perfil</h1>
        <form onSubmit={handleSubmit} className="bg-gray-900 p-6 rounded-md shadow-md">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            <div className="relative mb-4">
              <label htmlFor="profileImage" className="block text-sm font-medium text-white">Imagem de Perfil</label>
              <img src={user.FotoPerfil ? user.FotoPerfil : ProfileDefault} alt="Profile" className="w-full h-40 object-cover rounded-md" />
              <input type="file" id="profileImage" accept="image/*" onChange={handleProfileImageChange} className="absolute bottom-2 right-2 z-10" />
            </div>
            <div className="relative mb-4">
              <label htmlFor="coverImage" className="block text-sm font-medium text-white">Imagem de Capa</label>
              <img src={user.FotoCapa ? user.FotoCapa : CapaDefault} alt="Cover" className="w-full h-40 object-cover rounded-md" />
              <input type="file" id="coverImage" accept="image/*" onChange={handleCoverImageChange} className="absolute bottom-2 right-2 z-10" />
            </div>
            <div className="mb-4">
              <label htmlFor="titleUser" className="block text-sm font-medium text-white">Título</label>
              <input
                type="text"
                id="titleUser"
                value={titleUser}
                onChange={(e) => setTitleUser(e.target.value)}
                className="bg-gray-800 text-white p-2 rounded-md w-full"
                placeholder="Título"
              />
            </div>
            {userType === 'empresa' && (
              <>
                <div className="mb-4">
                  <label htmlFor="nifEmpresa" className="block text-sm font-medium text-white">NIF da Empresa</label>
                  <input
                    type="text"
                    id="nifEmpresa"
                    value={nifEmpresa}
                    onChange={(e) => setNifEmpresa(e.target.value)}
                    className="bg-gray-800 text-white p-2 rounded-md w-full"
                    placeholder="NIF da Empresa"
                    pattern="[0-9]{9}"
                    title="NIF deve conter 9 dígitos numéricos"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label htmlFor="nomeEmpresa" className="block text-sm font-medium text-white">Nome da Empresa</label>
                  <input
                    type="text"
                    id="nomeEmpresa"
                    value={nomeEmpresa}
                    onChange={(e) => setNomeEmpresa(e.target.value)}
                    className="bg-gray-800 text-white p-2 rounded-md w-full"
                    placeholder="Nome da Empresa"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label htmlFor="nomeRepresentante" className="block text-sm font-medium text-white">Nome do Representante</label>
                  <input
                    type="text"
                    id="nomeRepresentante"
                    value={nomeRepresentante}
                    onChange={(e) => setNomeRepresentante(e.target.value)}
                    className="bg-gray-800 text-white p-2 rounded-md w-full"
                    placeholder="Nome do Representante"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label htmlFor="anoExistencia" className="block text-sm font-medium text-white">Ano de Existência</label>
                  <input
                    type="number"
                    id="anoExistencia"
                    value={anoExistencia}
                    onChange={(e) => setAnoExistencia(e.target.value)}
                    className="bg-gray-800 text-white p-2 rounded-md w-full"
                    placeholder="Ano de Existência"
                    min="1900"
                    max={new Date().getFullYear()}
                    required
                  />
                </div>
              </>
            )}
            {userType === 'empreendedor' && (
              <>
                <div className="mb-4">
                  <label htmlFor="bi" className="block text-sm font-medium text-white">BI</label>
                  <input
                    type="text"
                    id="bi"
                    value={bi}
                    onChange={(e) => setBi(e.target.value)}
                    className="bg-gray-800 text-white p-2 rounded-md w-full"
                    placeholder="BI"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label htmlFor="nomeEmpreendedor" className="block text-sm font-medium text-white">Nome do Empreendedor</label>
                  <input
                    type="text"
                    id="nomeEmpreendedor"
                    value={nomeEmpreendedor}
                    onChange={(e) => setNomeEmpreendedor(e.target.value)}
                    className="bg-gray-800 text-white p-2 rounded-md w-full"
                    placeholder="Nome do Empreendedor"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label htmlFor="dataNascimento" className="block text-sm font-medium text-white">Data de Nascimento</label>
                  <input
                    type="date"
                    id="dataNascimento"
                    value={dataNascimento}
                    onChange={(e) => setDataNascimento(e.target.value)}
                    className="bg-gray-800 text-white p-2 rounded-md w-full"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label htmlFor="genero" className="block text-sm font-medium text-white">Gênero</label>
                  <select
                    id="genero"
                    value={genero}
                    onChange={(e) => setGenero(e.target.value)}
                    className="bg-gray-800 text-white p-2 rounded-md w-full"
                    required
                  >
                    <option value="">Selecione</option>
                    <option value="Masculino">Masculino</option>
                    <option value="Feminino">Feminino</option>
                    <option value="Outro">Outro</option>
                  </select>
                </div>
              </>
            )}
            <div className="flex justify-end gap-4 mb-4">
              {editing ? (
                <>
                  <button type="button" onClick={handleCancelClick} className="bg-gray-600 text-white px-4 py-2 rounded-md">Cancelar</button>
                  <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded-md">Salvar</button>
                </>
              ) : (
                <button type="button" onClick={() => setEditing(true)} className="bg-blue-600 text-white px-4 py-2 rounded-md">Editar</button>
              )}
            </div>
          </div>
        </form>
        {successDialogOpen && <SuccessDialog message={dialogMessage} onClose={handleCloseSuccessDialog} />}
        {errorDialogOpen && <ErrorDialog message={dialogMessage} onClose={handleCloseErrorDialog} />}
      </div>
    </PrincipalPage>
  );
};
