import React from 'react';
import { useFormik } from 'formik';
import * as yup from 'yup';
import { useDispatch, useSelector } from 'react-redux';
import { createPost } from './../../redux/postSlice';

const validationSchema = yup.object({
  title: yup.string().required('Título é obrigatório'),
  description: yup.string().required('Descrição é obrigatória'),
  imageUrl: yup.string().url('URL inválida').required('URL da imagem é obrigatória'),
});

export const CreatePost = () => {
  const dispatch = useDispatch();
  const postStatus = useSelector((state) => state.post.status);
  const postError = useSelector((state) => state.post.error);

  const formik = useFormik({
    initialValues: {
      title: '',
      description: '',
      imageUrl: '',
    },
    validationSchema: validationSchema,
    onSubmit: (values) => {
      dispatch(createPost(values));
    },
  });

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded-md shadow-md">
      <form onSubmit={formik.handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Título</label>
          <input
            type="text"
            name="title"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.title}
            className="w-full px-3 py-2 border rounded-md"
          />
          {formik.touched.title && formik.errors.title ? (
            <div className="text-red-500 text-sm">{formik.errors.title}</div>
          ) : null}
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">Descrição</label>
          <textarea
            name="description"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.description}
            className="w-full px-3 py-2 border rounded-md"
          />
          {formik.touched.description && formik.errors.description ? (
            <div className="text-red-500 text-sm">{formik.errors.description}</div>
          ) : null}
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">URL da Imagem</label>
          <input
            type="text"
            name="imageUrl"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.imageUrl}
            className="w-full px-3 py-2 border rounded-md"
          />
          {formik.touched.imageUrl && formik.errors.imageUrl ? (
            <div className="text-red-500 text-sm">{formik.errors.imageUrl}</div>
          ) : null}
        </div>

        <div className="flex justify-between items-center">
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded-md"
            disabled={postStatus === 'loading'}
          >
            {postStatus === 'loading' ? 'Postando...' : 'Postar'}
          </button>
        </div>

        {postStatus === 'succeeded' && (
          <div className="text-green-500 text-sm mt-4">Post criado com sucesso!</div>
        )}

        {postStatus === 'failed' && (
          <div className="text-red-500 text-sm mt-4">{postError}</div>
        )}
      </form>
    </div>
  );
};
