import React, { useState } from 'react';
import axios from 'axios';
import { Dialog, Transition } from '@headlessui/react';

export function Register() {
  // Estados para gerenciar os dados dos formulários
  const [usuarioData, setUsuarioData] = useState({
    Email: '',
    Endereco: '',
    Telefone: '',
    AreaAtuacao: '',
    Provincia: '',
    Senha: '',
    tipo: '', // 'empresa' ou 'empreendedor'
  });

  const [empresaData, setEmpresaData] = useState({
    NomeRepresentante: '',
    NomeEmpresa: '',
    NIF: '',
    AnosDeExistencia: '',
  });

  const [empreendedorData, setEmpreendedorData] = useState({
    Nome: '',
    Genero: '',
    DataNascimento: '',
    BI: '',
  });

  // Estados para o modal
  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState(null);

  // Função para lidar com a mudança de dados nos formulários
  const handleChange = (event) => {
    const { name, value } = event.target;
    if (name === 'tipo') {
      setUsuarioData({ ...usuarioData, [name]: value });
      if (value === 'empresa') {
        setEmpresaData({
          ...empresaData,
          NomeRepresentante: '',
          NomeEmpresa: '',
          NIF: '',
          AnosDeExistencia: '',
        });
      } else if (value === 'empreendedor') {
        setEmpreendedorData({
          ...empreendedorData,
          Nome: '',
          Genero: '',
          DataNascimento: '',
          BI: '',
        });
      }
    } else if (name.startsWith('empresaData.')) {
      const fieldName = name.replace('empresaData.', '');
      setEmpresaData({ ...empresaData, [fieldName]: value });
    } else if (name.startsWith('empreendedorData.')) {
      const fieldName = name.replace('empreendedorData.', '');
      setEmpreendedorData({ ...empreendedorData, [fieldName]: value });
    } else {
      setUsuarioData({ ...usuarioData, [name]: value });
    }
  };

  // Função para enviar os dados do formulário para a API
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Envia os dados do usuário para a API
      const response = await axios.post('http://localhost:3001/register', {
        ...usuarioData,
        ...(usuarioData.tipo === 'empresa' ? empresaData : empreendedorData),
      });
      console.log(response.data);
      // Limpa os formulários após o envio
      setUsuarioData({
        Email: '',
        Endereco: '',
        Telefone: '',
        AreaAtuacao: '',
        Provincia: '',
        Senha: '',
        tipo: '',
      });
      setEmpresaData({
        NomeRepresentante: '',
        NomeEmpresa: '',
        NIF: '',
        AnosDeExistencia: '',
      });
      setEmpreendedorData({
        Nome: '',
        Genero: '',
        DataNascimento: '',
        BI: '',
      });
      // Mostra o modal de sucesso
      setMessage({ text: 'Usuário registrado com sucesso!', type: 'success' });
      setIsOpen(true);
    } catch (error) {
