import React from 'react';
import CarexLogo from "../../assets/img/carex_logo.png";
import { FaThumbsUp, FaShare, FaChevronCircleRight } from 'react-icons/fa';

export function MainPost({ postagem }) {
    // Obtém os dados do usuário (empresa ou empreendedor)
    const usuario = postagem.Usuario;
    const nomeDoUsuario = usuario.Empresa ? usuario.Empresa.NomeEmpresa : (usuario.Empreendedor ? usuario.Empreendedor.Nome : 'Nome não disponível');
    const fotoDoUsuario = usuario.Empresa ? usuario.Empresa.Foto : (usuario.Empreendedor ? usuario.Empreendedor.Foto : CarexLogo); // Use CarexLogo como padrão se não houver foto

    return (
        <div className="containerPost bg-gray-900 border border-gray-700 rounded-md p-4">
            <div className="postagem p-4 rounded-lg border border-gray-200 mb-4">
                <div className="flex items-center gap-4">
                    <div className="avatar">
                        <img src={fotoDoUsuario} alt="Foto do autor" className="rounded-full" /> {/* Use a foto do usuário */}
                    </div>
                    <div className="autor font-medium text-gray-800">
                        {nomeDoUsuario} - {postagem.Data} {/* Use o nome do usuário */}
                    </div>
                </div>
                <div className="conteudo text-gray-200 mt-2">
                    {postagem.Conteudo}
                </div>
                {postagem.Foto && (
                    <img src={postagem.Foto} alt="Foto da postagem" className="w-full rounded-lg mt-4" />
                )}
                <div className="flex justify-evenly items-center mb-4">
                    <button className="flex items-center text-blue-400"><FaThumbsUp className="mr-2" /> Curtir</button>
                    <button className="flex items-center text-blue-400"><FaShare className="mr-2" /> Compartilhar</button>
                </div>
                <div className="bg-gray-800 rounded-md p-2 mb-2 flex justify-between">
                    <input type="text" placeholder='Comentar...' className="w-full h-10 rounded-full pl-4 border border-gray-600 bg-gray-600 text-white" />
                    <button className='text-blue-400 text-3xl ml-2'><FaChevronCircleRight /></button>
                </div>
                {/* Se você quiser exibir os comentários da postagem */}
                {postagem.Comentarios && postagem.Comentarios.map((comentario, index) => (
                    <div key={index} className="bg-gray-800 rounded-md p-2 mb-2">
                        <div className="flex justify-between text-gray-400 mb-2 text-sm">
                            <span className="text-white font-bold">{comentario.Autor}</span>
                            <button className="text-blue-400"><FaThumbsUp className="mr-2" /></button>
                        </div>
                        <p className="text-white text-sm">{comentario.Conteudo}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default MainPost;
