/* eslint-disable no-unused-vars */
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Register.css';
import { Logoform } from '../../components/logo';
import axios from 'axios';
import FormularioEmpresa from './FormularioEmpresa';
import FormularioEmpreendedor from './FormularioEmpreendedor';

export function Register() {
  const [tipo, setTipo] = useState('empresa'); 
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Envia todos os dados do formulário para a API
      const response = await axios.post('/register', new FormData(e.target)); 

      setLoading(false);
      // Redireciona para a página de login
      navigate('/login');
    } catch (error) {
      setLoading(false);
      if (error.response && error.response.status === 400) {
        setError(error.response.data.message || 'Erro na validação dos dados.');
      } else {
        setError('Erro ao registrar. Tente novamente mais tarde.');
      }
      console.error('Erro no registro:', error);
    }
  };

  return (
    <>
      <section className='mt-8  md:min-h-screen lg:min-h-screen'>
        <div className="min-h-screen bg-gray-900 flex flex-col items-center justify-center">
          <div className="flex justify-end mb-8">
            <Logoform className="w-10 h-10" /> 
          </div> 
          <h2 className="text-2xl font-bold text-white mb-6">Registro de Usuário</h2>

          {/* Seleção do tipo de registro */}
          <div className="flex gap-4 mb-4">
            <button
              onClick={() => setTipo('empresa')}
              className={`bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline ${tipo === 'empresa' ? 'bg-blue-600' : ''}`}
            >
              Empresa
            </button>
            <button
              onClick={() => setTipo('empreendedor')}
              className={`bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline ${tipo === 'empreendedor' ? 'bg-blue-600' : ''}`}
            >
              Empreendedor
            </button>           
          </div>

          {/* Exibir o formulário de empresa ou empreendedor */}
          {tipo === 'empresa' && <FormularioEmpresa />}
          {tipo === 'empreendedor' && <FormularioEmpreendedor />}

        </div>
      </section>
    </>
  );
};
